import React, { useState, useEffect } from "react";
import { collection, addDoc, getDocs, query, where } from "firebase/firestore";
import { auth, db } from "../init-firebase";
import Swal from "sweetalert2";
import { onAuthStateChanged, signOut } from "firebase/auth";
import { Helmet } from "react-helmet";
import '../styles/home.css'

const HomePage = () => {
  const [bloodGroup, setBloodGroup] = useState("");
  const [userData, setUserData] = useState({
    Name: "",
    Age: "",
    Contact: "",
  });

  // Fetch the user data from Firestore
  useEffect(() => {
    const fetchUserData = async () => {
      try {
        const user = auth.currentUser;
        if (user) {
          const q = query(collection(db, "Users"), where("Email", "==", user.email));
          const querySnapshot = await getDocs(q);

          querySnapshot.forEach((doc) => {
            const data = doc.data();
            setUserData({
              Name: data.Name,
              Age: data.Age,
              Contact: data.Contact,
            });
          });
        }
      } catch (error) {
        console.error("Error fetching user data:", error);
      }
    };

    fetchUserData();
  }, []);

  // Function to handle Blood Donation
  const handleDonate = async () => {
    if (!bloodGroup) {
      Swal.fire("Please enter your blood group!");
      return;
    }

    try {
      // Save the data to Firestore
      await addDoc(collection(db, "BloodDonors"), {
        Name: userData.Name,
        Age: userData.Age,
        Contact: userData.Contact,
        BloodGroup: bloodGroup,
        DonatedAt: new Date(),
      });

      Swal.fire("Thank you for donating!", "Your data has been saved.", "success");
      setBloodGroup("");
    } catch (error) {
      console.error("Error saving donation data:", error);
      Swal.fire("Error", "Failed to save your donation data.", "error");
    }
  };

  const DonateButton = async () => {
    if (!name || !bloodGroup || !city || !ContactNo) {
      Swal.fire("Please fill in all the fields before donating blood.");
      return;
    }

    // Call AddDonate to add the donation details
    AddDonate(bloodGroup, name, city, ContactNo);

    try {
      await addDoc(collection(db, "BloodDoners"), {
        DonerName: name,
        DonerBloodGroup: bloodGroup,
        CityName: city,
        ContactNo: ContactNo,
      });

      Swal.fire(`Thank you ${name} for your blood donation!`);
      setName("");
      setBloodGroup("");
      setCity("");
      setContactNo("");
    } catch (error) {
      console.error("Error donating blood:", error.message);
      Swal.fire("An error occurred while donating blood. Please try again.");
    }
  };

 
   
  // LogOut Function
  const LogOut = async () => {
    try {
      await signOut(auth);
      Swal.fire("Success", "You have been logged out.", "success");
      window.location.href = "/login";
    } catch (error) {
      console.error("Error during logout:", error);
      Swal.fire("Error", "Failed to log out. Please try again.", "error");
    }
  };

  return (
 <div className="homepage">
     <Helmet><title>HomePage</title>
     </Helmet>
    <div className="navbar">
         <div className="navbar-brand">Blood Bank</div>
         <div className="navbar-toggle" onClick="toggleNavbar()">
           <span></span>
          <span></span>
           <span></span>
         </div>
         <div className="navbar-links">
          <a href="/home">Home</a>
           <a href="/profile">Profile</a>           
           <button onClick={LogOut}>Logout</button>
         <a href="/doner">Doner's</a>
         </div>
       </div>

       <h1 className="title">Blood Bank</h1>
       <div className="form-container">
         <input
           type="text" placeholder="Enter Your Name" value={name} onChange={(e) => setName(e.target.value)}  />
         <input type="number"  placeholder="Enter Your Age" value={age} onChange={(e) => setAge(e.target.value)} />
         <input  type="text" placeholder="Enter Blood Group" value={bloodGroup} onChange={(e) => setBloodGroup(e.target.value)} />
         <input  type="text" placeholder="Enter City" value={city} onChange={(e) => setCity(e.target.value)} />         <input type="text" placeholder="Enter Contact NO"value={ContactNo}onChange={(e) => setContactNo(e.target.value)} />
           <div className="button-group">
           <button onClick={DonateButton} className="btn donate-btn">
             Donate Blood
           </button>
           
         </div>
       </div>
       <footer className="footer">
         <div className="footer-container">
           <p>&copy; {new Date().getFullYear()} Blood Bank | All Rights Reserved</p>
           <p>
           </p>
         </div>
       </footer>
     </div> 
  )}
    

export default HomePage;
