import React, { useState } from "react";
import { db, collection, getDocs } from "../init-firebase"; // Firebase imports
import '../styles/hero.css';

const Hero = () => {
  const [doners, setDoners] = useState([]); // State to store donors
  const [loading, setLoading] = useState(false); // Loading state
  const [showDonors, setShowDonors] = useState(false); // State to track when to show donor data

  // Function to fetch donors when "Find Donor" button is clicked
  const fetchDonors = async () => {
    setLoading(true); // Start loading
    setShowDonors(true); // Show donor list after fetching

    try {
      const donorCollection = collection(db, "donors"); // Reference to donors collection
      const donorSnapshot = await getDocs(donorCollection); // Get donor data
      const donorList = donorSnapshot.docs.map((doc) => doc.data()); // Map through the docs to get data
      setDoners(donorList); // Set the fetched donor data to state
    } catch (error) {
      console.error("Error fetching donors: ", error);
    } finally {
      setLoading(false); // Stop loading
    }
  };

  return (
    <div className="hero-container">
      <div className="hero-overlay"></div>
      <div className="hero-content">
        <h1 className="hero-title">
          Help Save Lives, Donate Blood Today!
        </h1>
        <p className="hero-description">
          Your donation can make a huge difference in someone's life.
        </p>
        <div className="hero-buttons">
          {/* Find Donor Button */}
          <button onClick={fetchDonors} className="hero-button find-button">
            Find Donor
          </button>
        </div>
      </div>

      {/* Show loading message */}
      {loading && <p className="loading-text">Loading donors...</p>}

      {/* Show donor data */}
      {showDonors && doners.length > 0 && (
        <div className="donor-list">
          {doners.map((doner, index) => (
            <div key={index} className="doner-card">
              <div className="doner-card-content">
                {/* Donor Name */}
                <h3 className="doner-name">{doner.Name || "N/A"}</h3>
                <p className="doner-info">Age: {doner.Age || "N/A"}</p>
                <p className="doner-info">Contact Number: {doner.ContactNumber || "N/A"}</p>
                <p className="doner-info">Blood Group: {doner.BloodGroup || "N/A"}</p>
              </div>
            </div>
          ))}
        </div>
      )}

      {/* If no donors are found */}
      {doners.length === 0 && !loading && showDonors && <p>No donors found.</p>}
    </div>
  );
};

export default Hero;
