import React, { useState } from "react";
import Swal from "sweetalert2";
import { collection, query, where, getDocs, addDoc } from "firebase/firestore";
import { db } from "../init-firebase";
import Mycontext from "../context/Mycontext";

const Hero= () => {
const [Name,setName]=useState('')
const [Age,setage]=useState('')
const [ContactNumber,setContactNumber]=useState('')
const [BloodGroup,setBloodGroup]=useState('')

 const DonateSubmit= async(e)=>{
  e.preventDefault()
  try {
    await db.collection('doners').add({
      Name,Age,ContactNumber,BloodGroup

    })
    alert('Doner Registered Successfully')

  }catch(error){
    
  }

 }

  return(
    <div>
      <label htmlFor="">Name:</label>
      <input type="text" name="Name" id="name" value={Name} onChange={(e)=>setName(e.target.value)} /><br/><br/>
      <label htmlFor="">Number:</label>
      <input type="number" name="number" id="number" value={ContactNumber} onChange={(e)=>setContactNumber(e.target.value)}/><br/><br/>
      
      <label htmlFor="">Age:</label>
      <input type="number" name="number" id="number" value={Age} onChange={(e)=>setage(e.target.value)} /><br/><br/>
      
      <label htmlFor="">Blood Group:</label>
      <input type="text" name="Name" id="bloodgroup"  value={BloodGroup} onChange={(e)=>setBloodGroup(e.target.value)}/><br/><br/>

    </div>
  )
};
export default Hero;
