import Swal from "sweetalert2";
import React from "react";
import { auth, createUserWithEmailAndPassword, signInWithEmailAndPassword, sendPasswordResetEmail, db } from "../init-firebase";
import Mycontext from "./Mycontext";
import { doc, setDoc } from "firebase/firestore";
import { useNavigate } from "react-router-dom";
const Myprovider = ({ children }) => {
  // Signup Function 
  const navigate=useNavigate()
  const SignupFirebase = async (name, email, password, url, ) => {
    if (name && email && password) {
      try {
        const userCredential = await createUserWithEmailAndPassword(auth, email, password);
        const user = userCredential.user;
  
        await setDoc(doc(db, "users", user.uid), {
          name: name,
          email: email,
          password: password,
          uid: user.uid,
          url:url,
          doners:doners,
          address: address,
          createdAt: new Date(),
        });
  
        Swal.fire("Wow brother, good job! User registered successfully.");
        console.log("User saved to Firestore:", user);
  
        // Debug and navigate
        console.log("Navigating to home");
        navigate("/home");
  
      } catch (error) {
        console.error("Error signing up:", error.message);
        Swal.fire("Error signing up. Please try again.");
      }
    } else {
      Swal.fire("Please fill all fields.");
    }
  };
  // SignIn Exist User

  const SignInfirebase = async (email, password) => {
    if (email && password) {
      try {
        const userCredential = await signInWithEmailAndPassword(auth, email, password);
        const user = userCredential.user;
        Swal.fire("Wow brother, Great! Signed in successfully.");
        console.log("Signed in user:", user);
        navigate('home')
      } catch (error) {
        console.error("Error signing in:", error.message);
        Swal.fire("Error signing In. Please try again.");
        navigate('/home')
      }
    } else {
      Swal.fire("Please fill all fields.");
    }
  };
  // Forget Password Function
  const ForgetPassword = async (email) => {
    if (email) {
      try {
        await sendPasswordResetEmail(auth, email);
        Swal.fire("Password reset email sent successfully!");
      } catch (error) {
        console.error("Error resetting password:", error.message);
        Swal.fire("Error resetting password. Please try again.");
      }
    } else {
      Swal.fire("Please provide your email.");
    }
  };

  // Provider Component Return
  return (
    <Mycontext.Provider value={{SignupFirebase,SignInfirebase,ForgetPassword }}
    >
      {children}
    </Mycontext.Provider>
  );
};

export default Myprovider;
