import React, { useContext, useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import { Helmet } from "react-helmet";
import Mycontext from "../context/Mycontext";
function Signup() {
  const { SignupFirebase } = useContext(Mycontext);
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [doners, SetDoner] = useState('');
  const navigate = useNavigate();  

  const SignupButton =() => {
    const success =  SignupFirebase(name, email, password,doners);
   
  };

  return (
    <div className="flex min-h-screen items-center justify-center bg-gray-100">
      <div className="w-full max-w-md bg-white rounded-lg shadow-md p-6">
        <h2 className="text-2xl font-bold text-gray-800 text-center">
          <Helmet> <title>Signup</title> </Helmet>Sign Up
        </h2>
        <center>
          <img src="https://thumbs.dreamstime.com/b/composite-image-blood-donation-against-thumbs-up-55533527.jpg" alt="" />
        </center>
        <div className="mb-4">
          <label className="block text-gray-700 text-sm font-medium" htmlFor="name">Name</label>
          <input
            type="text"
            id="name"
            className="w-full px-4 py-2 mt-2 border border-gray-300 rounded-lg focus:ring-blue-500 focus:border-blue-500"
            placeholder="Enter your name"
            value={name}
            onChange={(e) => setName(e.target.value)}
          />
        </div>

        <div className="mb-4">
          <label className="block text-gray-700 text-sm font-medium" htmlFor="email">Email</label>
          <input
            type="email"
            id="email"
            className="w-full px-4 py-2 mt-2 border border-gray-300 rounded-lg focus:ring-blue-500 focus:border-blue-500"
            placeholder="Enter your email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
        </div>
       
        <div className="mb-4">
          <label className="block text-gray-700 text-sm font-medium" htmlFor="password">Password</label>
          <input type="password" id="password" className="w-full px-4 py-2 mt-2 border border-gray-300 rounded-lg focus:ring-blue-500 focus:border-blue-500"
            placeholder="Create a password"
            value={password} onChange={(e) => setPassword(e.target.value)}/>
        </div>
           <div className="mb-4">
           <label className="block text-gray-700 text-sm font-medium" htmlFor="role">Who Are You?</label>
           <select id="role" className="w-full px-4 py-2 mt-2 border border-gray-300 rounded-lg focus:ring-blue-500 focus:border-blue-500"
            value={doners} onChange={(e) => SetDoner(e.target.value)}>
                <option value="" disabled>Select your role</option>
                <option value="Donor">Become A Doner</option>
                <option value="Receiver">Find Doner </option>
            </select>
        </div>
        <div className="mb-4">
          <button
            className="w-full bg-green-500 hover:bg-green-600 text-white py-2 px-4 rounded-lg" onClick={SignupButton}>
            Sign Up
          </button>
        </div>
        <p className="text-sm text-center text-gray-600">
          Already have an account?{' '}
          <Link to="/login" className="text-green-500 hover:underline">
            Sign In
          </Link>
        </p>
      </div>
    </div>
  );
}

export default Signup;
