import React, { useEffect, useState } from "react";
import { collection, db } from "../init-firebase";
import { getDocs } from "firebase/firestore";

const Reciever = () => {
  const [Doners, setDoners] = useState([]);
  const [loading, setLoading] = useState(true); // To handle loading state

  useEffect(() => {
    const GetDonersdata = async () => {
      try {
        const donerCollection = collection(db, "doners");
        const donerSnapshot = await getDocs(donerCollection);

        // Map donor data and save it in state
        const donerList = donerSnapshot.docs.map((doc) => doc.data());
        setDoners(donerList);
      } catch (error) {
        console.error("Error fetching donor data: ", error);
      } finally {
        setLoading(false); // Set loading to false once data is fetched or on error
      }
    };

    GetDonersdata();
  }, []);

  // Function to handle contacting the donor
  const contactDonor = (contactNumber) => {
    // Example: Use phone number for contact
    alert(`You can contact the donor at: ${contactNumber}`);
    // Or open the phone dialer (useful on mobile)
    // window.location.href = `tel:${contactNumber}`;
  };

  return (
    <div>
      <center>
        <h1>Available Donor Data</h1>

        {/* Display Loading message if data is being fetched */}
        {loading && <p>Loading donors...</p>}
        {Doners.length === 0 && !loading && <p>No donors found.</p>}

        {/* Display donor data if available */}
        {Doners.map((doner, index) => (
          <div key={index} className="donor" style={{ marginBottom: "20px" }}>
            <h3>Name: {doner.Name || "N/A"}</h3>
            <p>Age: {doner.Age || "N/A"}</p>
            <p>Contact Number: {doner.ContactNumber || "N/A"}</p>
            <p>Blood Group: {doner.BloodGroup || "N/A"}</p>
            <button onClick={() => contactDonor(doner.ContactNumber)}>
              Contact Donor
            </button>
          </div>
        ))}
      </center>
    </div>
  );
};

export default Reciever;
