import React, { useState, useEffect } from "react";
import { collection, addDoc, getDocs, query, where, onSnapshot, getDoc, doc, updateDoc } from "firebase/firestore";
import { db } from "../init-firebase";
import Swal from "sweetalert2";

import { Helmet } from "react-helmet";
import '../styles/home.css';
const HomePage = () => {
  const [bloodGroup, setBloodGroup] = useState("");
  const [city, setCity] = useState("");
  const [name, setName] = useState("");
  const [age, setAge] = useState("");
  const [canDonate,setCanDonate]=useState('')
  const [ContactNo, setContactNo] = useState("");
  const [donors, setDonors] = useState([]);

    useEffect(() => {
      const fetchDonors = async () => {
      try {
      const donorsCollection = collection(db, "BloodDoners");

        // Using onSnapshot for real-time updates
        const unsubscribe = onSnapshot(donorsCollection, (snapshot) => {
          const donorData = snapshot.docs.map((doc) => doc.data());
          setDonors(donorData);  
        });

        // Cleanup the subscription when the component unmounts
        return unsubscribe;
      } catch (error) {
        console.error("Error fetching donors:", error);
        Swal.fire("Error", "Failed to fetch donors data", "error");
      }
    };

    fetchDonors();
  }, []);  // Empty dependency array means it runs only once after the component mounts

  // Function to handle donation
  const DonateButton = async (userId) => {
    
    const userRef=doc(db,"SignupUser",userId);
    const userSnap= await getDoc(userRef)
    if (userSnap.exists()) {
      const Userdata=userSnap.data()
      if (Userdata.DonatedBlood) {
        Swal.fire("You have already donated blood. Please sign up again to donate again.");
        return;
        
      }
      
    }
    
   
    try {
      // Adding a new donor to the BloodDoners collection
      await addDoc(collection(db, "BloodDoners"), {
        DonerName: name,
        DonerAge: age,
        DonerBloodGroup: bloodGroup,
        ContacNo: ContactNo,
        CityName: city,
      });
       updateDoc(userRef,{DonatedBlood:true})
      Swal.fire(`Thank you ${name} for donating blood!`);

      // Fetch the updated list of donors after donation
      const donorsCollection = collection(db, "BloodDoners");
      const querySnapshot = await getDocs(donorsCollection);
      const updatedDonors = querySnapshot.docs.map((doc) => doc.data());
      setDonors(updatedDonors);  // Update donors state to reflect new data
    } catch (error) {
      console.error("Error saving data:", error);
      Swal.fire("Error", "Failed to save data", "error");
    }
  };
  useEffect(() => {
    const checkDonationStatus = async (userId) => {
      const userRef = doc(db, "SinupUser", userId);
      const userSnap = await getDoc(userRef);
  
      if (userSnap.exists()) {
        const userData = userSnap.data();
        setCanDonate(!userData.DonatedBlood); // Enable button only if hasDonated is false
      }
    };
  
    checkDonationStatus(curre.uid); // Replace `currentUser.uid` with logged-in user's ID
  }, [currentUser]);
  
  // Function to find donors based on selected blood group and city
  const FindDoner = async () => {
    if (!bloodGroup || !city) {
      Swal.fire("Please add both city and blood group");
      return;
    }

    try {
      const matchData = query(
        collection(db, "BloodDoners"),
        where("DonerBloodGroup", "==", bloodGroup),
        where("CityName", "==", city)
      );

      const querySnapshot = await getDocs(matchData);
      const matchedDonors = querySnapshot.docs.map((doc) => ({
        name: doc.data().DonerName,
        age: doc.data().DonerAge,
        bloodGroup: doc.data().DonerBloodGroup,
        contactNo: doc.data().ContacNo,
        city: doc.data().CityName,
      }));
      setDonors(matchedDonors);  // Update donor list based on search criteria
    } catch (error) {
      console.error("Error fetching donors:", error);
      Swal.fire("Error", "Failed to fetch data", "error");
    }
  };

  return (
    <div className="homepage">
      <Helmet><title>HomePage</title></Helmet>
      <div className="navbar">
        <div className="navbar-brand">Blood Bank</div>
        <div className="navbar-toggle" onClick="toggleNavbar()">
          <span></span>
          <span></span>
          <span></span>
        </div>
        <div className="navbar-links">
          <a href="#">Home</a>
          <a href="#">Donate</a>
          <a href="/profile">Profile</a>
          <a href="#">Contact Us</a>
        </div>
      </div>

      <h1 className="title">Blood Bank</h1>
      <div className="form-container">
        <input
          type="text"
          placeholder="Enter Your Name"
          value={name}
          onChange={(e) => setName(e.target.value)}
        />
        <input
          type="number"
          placeholder="Enter Your Age"
          value={age}
          onChange={(e) => setAge(e.target.value)}
        />
        <input
          type="text"
          placeholder="Enter Blood Group"
          value={bloodGroup}
          onChange={(e) => setBloodGroup(e.target.value)}
        />
        <input
          type="text"
          placeholder="Enter City"
          value={city}
          onChange={(e) => setCity(e.target.value)}
        />
        <input
          type="text"
          placeholder="Enter Contact NO"
          value={ContactNo}
          onChange={(e) => setContactNo(e.target.value)}
        />

        <div className="button-group">
          <button onClick={()=>DonateButton(currentUser)}
          disabled={!canDonate} 
          className={`btn donate-btn ${canDonate ? "enabled" : "disabled"}`}>
            Donate Blood
          </button>
          <button onClick={FindDoner} className="btn find-btn">
            Find Donor
          </button>
        </div>
      </div>

      <div className="donor-list">
        <h2>Donor List</h2>
        {donors.length > 0 ? (
          donors.map((doner, index) => (
            <div key={index} className="donor-card">
              <p><strong>Name:</strong> {doner.DonerName}</p>
              <p><strong>Age:</strong> {doner.DonerAge}</p>
              <p><strong>Contact No:</strong> {doner.ContacNo}</p>
              <p><strong>Blood Group:</strong> {doner.DonerBloodGroup}</p>
              <p><strong>City:</strong> {doner.CityName}</p>
            </div>
          ))
        ) : (
          <p>No donors found</p>
        )}
      </div>
    </div>
  );
};

export default HomePage;
