import React, { useState } from "react";
import { collection, addDoc, getDocs, query, where } from "firebase/firestore";
import '../styles/home.css'
import { db } from "../init-firebase";
import Swal from "sweetalert2";



  const HomePage = () => {
  const [bloodGroup, setBloodGroup]  = useState("");
  const [city, setCity]              = useState("");
  const [name, setName]              = useState("");
  const [age, setAge]                = useState("");
  const [ContactNo,setContactNo]     =useState('')
  const [donors, setDonors]          = useState([]);

  const DonateButton = async () => {
    if (!name || !age || !bloodGroup || !city) {
      alert("Please fill in all fields!");
      return;
    }

  //   try {
  //     await addDoc(collection(db, "BloodDoners"), {
  //       DonerName: name,
  //       DonerAge: age,
  //       DonerBloodGroup: bloodGroup,
  //       ContacNo: ContactNo,
  //     });
  //     Swal.fire(`Thank U ${name} for Donating Blood`);
  //   } catch (error) {
  //     console.error("Error saving data:", error.message); // Log the error
  //     alert("Failed to save data. Check console for details.");
  //   }
  }    

  const FindDoner = async () => {
    if (!bloodGroup || !city) {
      Swal.fire("Please Add city and Blood group Both")
      alert("Please enter blood group and city!");
      return;
    }

    try {
      const matchdata = query(
        collection(db, "donors"),
        where("bloodGroup", "==", bloodGroup),where("city", "==", city)
      );
      const querySnapshot = await getDocs(matchdata);
      const matchedDonors = querySnapshot.docs.map((doc) => doc.data());
      setDonors(matchedDonors);
    } catch (error) {
      console.error("Error data:", error);
    }
  };

  return (
   <div className="homepage">
      <h1 className="title">Blood Bank</h1>
      <div className="form-container">
        <input   type="text"   placeholder="Enter Your Name"   value={name} onChange={(e) => setName(e.target.value)} />
        <input   type="number" placeholder="Enter Your Age"    value={age}onChange={(e) => setAge(e.target.value)} />
        <input   type="text"   placeholder="Enter Blood Group" value={bloodGroup} onChange={(e) => setBloodGroup(e.target.value)}/>
        <input   type="text"   placeholder="Enter City"        value={city} onChange={(e) => setCity(e.target.value)}/>
        <input   type="text"   placeholder="Enter Contact NO"  value={ContactNo} onChange={(e) => setContactNo(e.target.value)}/>

        <div className="button-group">
          <button onClick={DonateButton} className="btn donate-btn">
            Donate Blood
          </button>
          <button onClick={FindDoner} className="btn find-btn">
            Find Donor
          </button>
        </div>
      </div>

      <div className="donor-list">
        <h2>Doner List </h2>
        
        {donors.map((doner, index) => (
          <div key={index} className="donor-card">
            <p><strong>Name:</strong> {doner.name}
          </p>
            <p><strong>Age:</strong> {doner.age}
          </p>
            <p><strong>Blood Group:</strong> {doner.bloodGroup}
          </p>
            <p><strong>City:</strong> {doner.city}
          </p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default HomePage;
