import React, { useEffect, useState } from "react";
import { getDocs, collection } from "firebase/firestore";
import { db } from "../init-firebase";  // Make sure you import your Firestore instance correctly

const UserList = () => {
  const [users, setUsers] = useState([]);
  const [loading, setLoading] = useState(true);

  // Fetch users from Firestore
  useEffect(() => {
    const fetchUsers = async () => {
      try {
        const querySnapshot = await getDocs(collection(db, "users"));
        const usersData = querySnapshot.docs.map(doc => doc.data());
        setUsers(usersData);  // Store the data in state
        setLoading(false);     // Set loading to false after data is fetched
      } catch (error) {
        console.error("Error fetching users: ", error);
        setLoading(false);
      }
    };

    fetchUsers();
  }, []);  // Empty dependency array means it runs once after component mounts

  if (loading) {
    return <p>Loading...</p>;  // Loading state
  }

  return (
    <div>
      <h2>Users List</h2>
      {users.length > 0 ? (
        users.map((user, index) => (
          <div key={index}>
            <p>Name: {user.name}</p>
            <p>Email: {user.email}</p>
            <p>Blood Group: {user.bloodGroup}</p>
            <p>Address: {user.address}</p>
            {user.url && <img src={user.url} alt={user.name} />}
          </div>
        ))
      ) : (
        <p>No users found.</p>
      )}
    </div>
  );
};

export default UserList;
