import React, { useState } from "react";
import { collection, addDoc, getDocs, query, where } from "firebase/firestore";
import { auth, db } from "../init-firebase";
import Swal from "sweetalert2";
import { Helmet } from "react-helmet";
import Footer from './Footer'
import { useNavigate } from "react-router-dom";
import NavbaR from "./navbar";
import '../styles/bloodsec.css'
const HomePage = () => {
        const [name, setName] = useState("");
        const [age, setAge] = useState("");
        const [bloodGroup, setBloodGroup] = useState("");
        const [city, setCity] = useState("");
        const [ContactNo, setContactNo] = useState("");
        const [isNavbarOpen, setIsNavbarOpen] = useState(false);
        const Navigate=useNavigate()
        const DonateButton = async () => {
          if (!name || !bloodGroup || !city  ) {
            Swal.fire("Please fill in all the inputs before you donate blood.");
            return;
          }
          if (ContactNo.length !== 11) {
            Swal.fire("Contact number must be exactly 11 characters long.");
            return;
          }
          if (age < 22 || age > 80) {
            Swal.fire("Age must be between 22 and 80 years.");
            return;
          }
          
    const user = auth.currentUser;
    if (!user) {
      Swal.fire("Please log in to donate blood.");
      return;
    }
  
    try {
      const q = query(
        collection(db, "BloodDonors"),
        where("Email", "==", user.email)
      );
      const donorSnapshot = await getDocs(q);
  
      if (!donorSnapshot.empty) {
        Swal.fire("You have already donated blood once.");
        return;
      }
  
      await addDoc(collection(db, "BloodDonors"), {
        DonerName: name,
        DonerBloodGroup: bloodGroup,
        CityName: city,
        ContactNo: ContactNo,
        Age: age,
        Email: user.email,
        DonationDate: new Date()
      });
  
      Swal.fire(`Thank you ${name} from ${city} for your blood donation!`);
      setName("");
      setBloodGroup("");
      setCity("");
      setContactNo("");
      setAge("");
    } catch (error) {
      console.error("Error donating blood:", error.message); // Log the detailed error message
      Swal.fire("An error occurred while donating blood. Please try again.");
    }
  };
  
  
  
  const toggleNavbar = () => {
    setIsNavbarOpen(!isNavbarOpen);
  };
  return (
    <div><NavbaR/>
    <div className="homepage">
      <Helmet>
        <title>HomePage</title>
      </Helmet>
      
     <h1 className="title">Blood Bank</h1>
      <div className="form-container">
        <input
          type="text"
          placeholder="Enter Your Name"
          value={name}
          onChange={(e) => setName(e.target.value)}
        />
        <input
          type="number"
          placeholder="Enter Your Age"
          value={age}
          onChange={(e) => setAge(e.target.value)}
        />
        <input
          type="text"
          placeholder="Enter Blood Group"
          value={bloodGroup}
          onChange={(e) => setBloodGroup(e.target.value)}
        />
        <input
          type="text"
          placeholder="Enter City"
          value={city}
          onChange={(e) => setCity(e.target.value.toLowerCase())}
        />
        <input
          type="text"
          placeholder="Enter Contact No"
          value={ContactNo}
          onChange={(e) => setContactNo(e.target.value)}
        />
        <div className="button-group">
          <button onClick={DonateButton} className="btn donate-btn">
            Donate Blood
          </button>
        
        </div>
         </div>

       <Footer/>
    </div>
    </div>
  );
};

export default HomePage;
