import React, { useState } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import { auth, storage } from '../init-firebase'; 
import { updateProfile } from 'firebase/auth';
import { uploadBytesResumable, ref, getDownloadURL } from 'firebase/storage';

import Swal from 'sweetalert2';

const Profile = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const user = location.state?.user;
  const [profileImage, setProfileImage] = useState(user?.photoURL || '');
  const [uploading, setUploading] = useState(false);

  if (!user) {
    navigate('/'); // Redirect to login if no user data
    return null;
  }

  const handleProfilePictureUpload = async (e) => {
    const file = e.target.files[0];
    if (file) {
      if (file.size > 2 * 1024 * 1024) { // Limit file size to 2 MB
        Swal.fire("Error", "File size should not exceed 2 MB", "error");
        return;
      }
  
      setUploading(true);
  
      const storageRef = ref(storage, `profile_pictures/${user.email}`);
      try {
        const uploadTask = uploadBytesResumable(storageRef, file);
  
        uploadTask.on(
          'state_changed',
          (snapshot) => {
            // Optional: Progress tracking
            const progress = (snapshot.bytesTransferred / snapshot.totalBytes) * 100;
            console.log(`Upload is ${progress}% done`);
          },
          (error) => {
            console.error('Upload error:', error.message);
            Swal.fire("Error", error.message, "error");
          },
          async () => {
            // Upload completed successfully
            const url = await getDownloadURL(uploadTask.snapshot.ref);
  
            await updateProfile(auth.currentUser, { photoURL: url });
            setProfileImage(url);
  
            Swal.fire('Profile picture updated successfully!', '', 'success');
          }
        );
      } catch (error) {
        console.error('Error uploading profile picture:', error.message);
        Swal.fire('Error', error.message, 'error');
      } finally {
        setUploading(false);
      }
    }
  };

  return (
    <div className="flex flex-col items-center justify-center min-h-screen bg-gray-100">
      <div className="w-full max-w-lg bg-white rounded-lg shadow-lg p-6">
        <h2 className="text-3xl font-bold text-center text-gray-800">Your Profile</h2>
        <div className="mt-6 flex flex-col items-center">
          {/* Profile Picture */}
          <div className="relative w-32 h-32">
            <img
              src={profileImage || 'https://via.placeholder.com/150'}
              alt="Profile"
              className="w-full h-full rounded-full border border-gray-300"
            />
            <label
              htmlFor="upload"
              className="absolute bottom-0 right-0 bg-blue-500 text-white p-2 rounded-full cursor-pointer hover:bg-blue-600"
            >
              <svg
                xmlns="http://www.w3.org/2000/svg"
                fill="none"
                viewBox="0 0 24 24"
                strokeWidth="1.5"
                stroke="currentColor"
                className="w-5 h-5"
              >
                <path
                  strokeLinecap="round"
                  strokeLinejoin="round"
                  d="M12 15v3m0 0l1.5-1.5M12 18l-1.5-1.5m1.5-10.5v3m-6 0h12m-3-3v3m0 0H9m6 0h3m-6 0H6m9 0l-1.5-1.5m0 3L12 6m0 0L10.5 7.5m0-3L12 6"
                />
              </svg>
            </label>
            <input
              id="upload"
              type="file"
              accept="image/*"
              className="hidden"
              onChange={handleProfilePictureUpload}
            />
          </div>

          {/* User Details */}
          <h3 className="text-xl font-medium mt-4">{user.displayName || 'No Name Provided'}</h3>
          <p className="text-gray-600">{user.email}</p>

          {/* Loader for Upload */}
          {uploading && <p className="text-blue-500 mt-2">Uploading...</p>}

          {/* Actions */}
          <button
            onClick={() => navigate('/home')}
            className="mt-6 w-full bg-green-500 hover:bg-green-600 text-white py-2 px-4 rounded-lg"
          >
            Go to Home
          </button>
          <button
            onClick={() => {
              auth.signOut();
              navigate('/');
            }}
            className="mt-4 w-full bg-red-500 hover:bg-red-600 text-white py-2 px-4 rounded-lg"
          >
            Logout
          </button>
        </div>
      </div>
    </div>
  );
};

export default Profile;
