import React, { useState, useEffect } from "react";
import { collection, addDoc, getDocs, query, where } from "firebase/firestore";
import { auth, db } from "../init-firebase";
import Swal from "sweetalert2";
import { onAuthStateChanged, signOut } from "firebase/auth";
import { Helmet } from "react-helmet";
import "../styles/home.css";
const HomePage = () => {
  const [bloodGroup, setBloodGroup] = useState("");
  
  const [name, setName] = useState("");
  const [age, setAge] = useState("");
  const [contactNo, setContactNo] = useState("");
  const [email, setEmail] = useState("");

  // Fetch user details on page load
 
  const DonateBlood = async (bloodGroup) => {
    onAuthStateChanged(auth, async (user) => {
      if (user) {
        const currentUserEmail = user.email.trim()
        console.log("Logged-in user email:", currentUserEmail);
    
        try {
          // Query Firestore for the user
          const userQuery = query(
            collection(db, "SinupUser"),
            where("UserEmail", "==", currentUserEmail)
          );
    
          const userSnapshot = await getDocs(userQuery);
    
          if (userSnapshot.empty) {
            console.log("No matching documents in Firestore.");
            Swal.fire("User details not found in the database!", "", "error");
            return;
          }
    
          // Log the found user for confirmation
          const userData = userSnapshot.docs[0].data();
          console.log("User details found:", userData);
    
          // Further logic here (e.g., saving blood group)
          Swal.fire("User details found! Proceeding...", "", "success");
        } catch (error) {
          console.error("Error fetching user data:", error.message);
          Swal.fire("An error occurred. Please try again.", "", "error");
        }
      } else {
        console.log("No user logged in.");
        Swal.fire("Please log in to proceed!", "", "info");
      }
    });}
  return (
    <div className="homepage">
      <Helmet>
        <title>HomePage</title>
      </Helmet>
      <div className="navbar">
        <div className="navbar-brand">Blood Bank</div>
        <div className="navbar-links">
          <a href="/home">Home</a>
          <a href="/profile">Profile</a>
          <a href="/doner">Donors</a>
          <button onClick={LogOut}>Logout</button>
        </div>
      </div>

      <h1 className="title">Blood Bank</h1>
      <div className="form-container">
        <input type="text" placeholder="Name" value={name} onChange={(e)=>setName(e.target.value)} disabled />
        <input type="number" placeholder="Age" value={age} disabled />
        <input type="text" placeholder="Contact No" value={contactNo} disabled />
        <input
          type="text"
          placeholder="Enter Blood Group"
          value={bloodGroup}
          onChange={(e) => setBloodGroup(e.target.value)}
        />
        <div className="button-group">
          <button onClick={DonateBlood} className="btn donate-btn">
            Donate Blood
          </button>
        </div>
      </div>

      <footer className="footer">
        <div className="footer-container">
          <p>&copy; {new Date().getFullYear()} Blood Bank | All Rights Reserved</p>
        </div>
      </footer>
    </div>
  );
};

export default HomePage;
