import React, { useState } from "react";
import { setDoc, doc, arrayUnion, query, where, getDocs, collection } from "firebase/firestore";
import Swal from "sweetalert2";
import { db } from "../init-firebase";

const Hero = () => {
    const [selectedCity, setSelectedCity] = useState("");
    const [selectedBloodGroup, setSelectedBloodGroup] = useState("");
    const [matchingReceivers, setMatchingReceivers] = useState([]);

    const handleCityChange = (e) => {
        setSelectedCity(e.target.value);
      };
    
      const handleBloodGroupChange = (e) => {
        setSelectedBloodGroup(e.target.value);
      };
    
      const handleDonateClick = async () => {
        try {
          if (selectedCity && selectedBloodGroup) {
            // Query Firestore for matching receivers
            const q = query(
              collection(db, "users"),
              where("bloodGroup", "==", selectedBloodGroup),
              where("city", "==", selectedCity)
            );
    
            const querySnapshot = await getDocs(q);
            const receivers = querySnapshot.docs.map(doc => doc.data());
    
            if (receivers.length > 0) {
              setMatchingReceivers(receivers);
    
              // Log donor details to Firestore (optional)
              const donorRef = doc(db, "users", "donor-email@example.com");
              await setDoc(donorRef, {
                donations: arrayUnion({
                  bloodGroup: selectedBloodGroup,
                  city: selectedCity,
                  timestamp: new Date(),
                }),
              }, { merge: true });
    
              Swal.fire("Success!", "Matching receiver(s) found, donation successful.", "success");
            } else {
              Swal.fire("No Match", "No matching receivers found in the selected city.", "info");
            }
          } else {
            Swal.fire("Error", "Please select both a city and a blood group.", "error");
          }
        } catch (error) {
          console.error("Error finding matching receivers: ", error);
          Swal.fire("Error", "Something went wrong.", "error");
        }
      };
  return (
    <section className="hero">
    <div className="hero-text">
      <h2>Donate Blood to Save Lives</h2>
      <p>Select your city and blood group to find matching receivers.</p>

      {/* City Dropdown */}
      <div className="dropdown-container">
        <label htmlFor="city">City:</label>
        <select id="city" value={selectedCity} onChange={handleCityChange}>
          <option value="">Select City</option>
          <option value="Karachi">Karachi</option>
          <option value="Lahore">Lahore</option>
          <option value="Islamabad">Islamabad</option>
          <option value="Peshawar">Peshawar</option>
          <option value="Quetta">Quetta</option>
        </select>
      </div>

      {/* Blood Group Dropdown */}
      <div className="dropdown-container">
        <label htmlFor="blood-group">Blood Group:</label>
        <select id="blood-group" value={selectedBloodGroup} onChange={handleBloodGroupChange}>
          <option value="">Select Blood Group</option>
          <option value="A+">A+</option>
          <option value="A-">A-</option>
          <option value="B+">B+</option>
          <option value="B-">B-</option>
          <option value="O+">O+</option>
          <option value="O-">O-</option>
          <option value="AB+">AB+</option>
          <option value="AB-">AB-</option>
        </select>
      </div>

      <button onClick={handleDonateClick} className="cta-btn">
        Donate Blood
      </button>
    </div>

    {/* Render Matching Receivers */}
    {matchingReceivers.length > 0 && (
      <div className="matching-receivers">
        <h3>Matching Receivers</h3>
        <div className="receiver-cards">
          {matchingReceivers.map((receiver, index) => (
            <div key={index} className="receiver-card">
              <h4>{receiver.name}</h4>
              <p>Blood Group: {receiver.bloodGroup}</p>
              <p>City: {receiver.city}</p>
              <p>Contact: {receiver.contact}</p>
            </div>
          ))}
        </div>
      </div>
    )}
  </section>
);
};

export default Hero;


export {Hero};
