import React, { useState } from "react";
import Swal from "sweetalert2";
import { collection, query, where, getDocs, addDoc } from "firebase/firestore";
import { db } from "../init-firebase";

const Hero= () => {
  const [city, setCity] = useState("");
  const [bloodGroup, setBloodGroup] = useState("");
  const [results, setResults] = useState([]);
  const [actionType, setActionType] = useState("");

  const handleButtonClick = async (action) => {
    const { value: formValues } = await Swal.fire({
      title: `${action === "donate" ? "Donate Blood" : "Receive Blood"}`,
      html: `
        <input id="swal-input1" class="swal2-input" placeholder="Enter your city" value="${city}" />
        <input id="swal-input2" class="swal2-input" placeholder="Enter your blood group (e.g., A+, O-)" value="${bloodGroup}" />
      `,
      focusConfirm: false,
      showCancelButton: true,
      confirmButtonText: "Submit",
      preConfirm: () => {
        const updatedCity = document.getElementById("swal-input1").value;
        const updatedBloodGroup = document.getElementById("swal-input2").value;

        if (!updatedCity || !updatedBloodGroup) {
          Swal.showValidationMessage("Please fill out both fields!");
        }
        return { city: updatedCity, bloodGroup: updatedBloodGroup };
      },
    });

    if (formValues) {
      setCity(formValues.city);
      setBloodGroup(formValues.bloodGroup);

      
      try {
        if (action === "donate") {
          const receiverQuery = query(
            collection(db, "receivers"),
            where("city", "==", formValues.city),
            where("bloodGroup", "==", formValues.bloodGroup)
          );

          const receiverSnapshot = await getDocs(receiverQuery);
          if (!receiverSnapshot.empty) {
            const receivers = [];
            receiverSnapshot.forEach((doc) => receivers.push(doc.data()));
            setResults(receivers);
            setActionType("donate");
          } else {
            Swal.fire({
              icon: "info",
              title: "No Match Found",
              text: "Your details have been saved. We will notify you if a match is found.",
            });

            await addDoc(collection(db, "donors"), {
              city: formValues.city,
              bloodGroup: formValues.bloodGroup,
              donatedAt: new Date(),
            });
            setResults([]);
          }
        } else if (action === "receive") {
          const donorQuery = query(
            collection(db, "donors"),
            where("city", "==", formValues.city),
            where("bloodGroup", "==", formValues.bloodGroup)
          );

          const donorSnapshot = await getDocs(donorQuery);
          if (!donorSnapshot.empty) {
            const donors = [];
            donorSnapshot.forEach((doc) => donors.push(doc.data()));
            setResults(donors);
            setActionType("receive");
          } else {
            Swal.fire({
              icon: "info",
              title: "No Donors Found",
              text: "Currently, there are no donors matching your request.",
            });
            setResults([]);
          }
        }
      } catch (error) {
        console.error("Error:", error);
        Swal.fire({
          icon: "error",
          title: "An Error Occurred",
          text: "Please try again later.",
        });
      }
    }
  };

  return (
    <div className="home-container text-center py-10">
      <h1 className="text-4xl font-bold mb-6">Welcome to Blood Bank</h1>
      <p className="text-lg mb-8">Donate or Receive blood with ease!</p>

      <div className="space-x-4">
        {/* Donate Button */}
        <button
          onClick={() => handleButtonClick("donate")}
          className="bg-red-500 text-white px-6 py-2 rounded-lg hover:bg-red-600"
        >
          Donate Blood
        </button>

        {/* Receive Button */}
        <button
          onClick={() => handleButtonClick("receive")}
          className="bg-blue-500 text-white px-6 py-2 rounded-lg hover:bg-blue-600"
        >
          Receive Blood
        </button>
      </div>

      {/* Display Matching Results */}
      {results.length > 0 && (
        <div className="mt-10">
          <h2 className="text-2xl font-semibold mb-4">
            {actionType === "donate" ? "Matching Receivers" : "Matching Donors"}
          </h2>
          <table className="table-auto w-full border-collapse border border-gray-300">
            <thead>
              <tr>
                <th className="border border-gray-300 px-4 py-2">Name</th>
                <th className="border border-gray-300 px-4 py-2">City</th>
                <th className="border border-gray-300 px-4 py-2">Blood Group</th>
                <th className="border border-gray-300 px-4 py-2">Contact</th>
              </tr>
            </thead>
            <tbody>
              {results.map((result, index) => (
                <tr key={index}>
                  <td className="border border-gray-300 px-4 py-2">{result.name || "Anonymous"}</td>
                  <td className="border border-gray-300 px-4 py-2">{result.city}</td>
                  <td className="border border-gray-300 px-4 py-2">{result.bloodGroup}</td>
                  <td className="border border-gray-300 px-4 py-2">{result.contact || "N/A"}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}

      {/* No Results Found */}
      {results.length === 0 && (
        <p className="mt-10 text-gray-500">No matching data available to display.</p>
      )}
    </div>
  );
};









export {Hero};
