import React, { useState } from "react";
import Swal from "sweetalert2";
import { collection, addDoc } from "firebase/firestore";
import { db } from "../init-firebase"; // Correct import

const Hero = () => {
  const [Name, setName] = useState('');
  const [Age, setAge] = useState('');
  const [ContactNumber, setContactNumber] = useState('');
  const [BloodGroup, setBloodGroup] = useState('');

  const DonateSubmit = async (e) => {
    e.preventDefault(); // Prevent the form from refreshing the page
    try {
      // Add donor data to Firestore using modular API
      await addDoc(collection(db, 'doners'), {
        Name,
        Age,
        ContactNumber,
        BloodGroup,
      });

      // Show a success alert using SweetAlert2
      Swal.fire({
        title: 'Success!',
        text: 'Donor Registered Successfully!',
        icon: 'success',
        confirmButtonText: 'Okay',
      });

      // Optionally reset form after submission
      setName('');
      setAge('');
      setContactNumber('');
      setBloodGroup('');

    } catch (error) {
      console.log('Error adding document: ', error);
      // Handle errors gracefully
      Swal.fire({
        title: 'Error!',
        text: 'There was an issue registering the donor. Please try again.',
        icon: 'error',
        confirmButtonText: 'Try Again',
      });
    }
  };

  return (
    <div>
      <center>
        <h2>Register as A Blood Donor</h2>
        <form >
          <label htmlFor="name">Name:</label>
          <input
            type="text"
            name="Name"
            id="name"
            value={Name}
            onChange={(e) => setName(e.target.value)}
          />
          <br /><br />
          
          <label htmlFor="number">Number:</label>
          <input
            type="number"
            name="number"
            id="number"
            value={ContactNumber}
            onChange={(e) => setContactNumber(e.target.value)}
          />
          <br /><br />
          
          <label htmlFor="age">Age:</label>
          <input
            type="number"
            name="age"
            id="age"
            value={Age}
            onChange={(e) => setAge(e.target.value)}
          />
          <br /><br />
          
          <label htmlFor="bloodgroup">Blood Group:</label>
          <input
            type="text"
            name="bloodgroup"
            id="bloodgroup"
            value={BloodGroup}
            onChange={(e) => setBloodGroup(e.target.value)}
          />
          <br /><br />
          
          <button o>Donate Blood</button>
        </form>
      </center>
    </div>
  );
};

export default Hero;
