import React, { useState } from "react";
import '../styles/home.css';
import '../styles/hero.css';

// Dummy data for Receivers (can replace with actual data from a backend or context)
const dummyReceivers = [
  { name: "John Doe", bloodGroup: "A+", location: "New York" },
  { name: "Jane Smith", bloodGroup: "O-", location: "Los Angeles" },
  { name: "Michael Lee", bloodGroup: "B+", location: "Chicago" },
  { name: "Anna Clark", bloodGroup: "AB-", location: "Miami" },
];

const Hero = () => {
  const [selectedBloodGroup, setSelectedBloodGroup] = useState('');
  const [showReceivers, setShowReceivers] = useState(false);

  // Handle blood group selection
  const handleBloodGroupChange = (e) => {
    setSelectedBloodGroup(e.target.value);
    setShowReceivers(false); // Hide receivers until donate button is clicked
  };

  // Handle donate button click
  const handleDonateClick = () => {
    if (selectedBloodGroup) {
      setShowReceivers(true);
    } else {
      alert("Please select a blood group first.");
    }
  };

  console.log("Selected Blood Group: ", selectedBloodGroup);

  return (
    <section className="hero">
      <div className="hero-text">
        <h2>Give Blood, Save Lives!</h2>
        <p>Your donation can make a difference to someone in need.</p>
        
        <div className="donate-section">
          <h2>Donate Blood</h2>
          <p>Choose your blood group</p>

          <div className="dropdown-container">
            <label htmlFor="blood-group">Blood Group: </label>
            <select
              id="blood-group"
              value={selectedBloodGroup}
              onChange={handleBloodGroupChange}
            >
              <option value="">Select Blood Group</option>
              <option value="A+">A+</option>
              <option value="A-">A-</option>
              <option value="B+">B+</option>
              <option value="B-">B-</option>
              <option value="O+">O+</option>
              <option value="O-">O-</option>
              <option value="AB+">AB+</option>
              <option value="AB-">AB-</option>
            </select>
          </div>

          <button className="cta-btn" onClick={handleDonateClick}>Donate Now</button>
          <button className="cta-btn">Find Donors</button>
        </div>

        {/* Conditionally render receivers based on selected blood group */}
        {showReceivers && selectedBloodGroup && (
          <div className="receivers">
            {dummyReceivers
              .filter((receiver) => receiver.bloodGroup === selectedBloodGroup)
              .map((receiver, index) => (
                <div key={index} className="receiver-card">
                  <h3>{receiver.name}</h3>
                  <p>Blood Group: {receiver.bloodGroup}</p>
                  <p>Location: {receiver.location}</p>
                </div>
              ))}
          </div>
        )}
      </div>
    </section>
  );
};

export default Hero;
