import React, { useState } from "react";
import Swal from "sweetalert2";
import {collection, addDoc, } from "firebase/firestore";
import { db } from "../init-firebase";



const RegistrationForm = () => {
    const [formData, setFormData] = useState({
      name: "",
      fatherName: "",
      address: "",
      phoneNumber: "",
    });
  
    const handleChange = (e) => {
      const { name, value } = e.target;
      setFormData({ ...formData, [name]: value });
    };
  
    const handleSubmit = async (e) => {
      e.preventDefault();
  
      const { name, fatherName, address, phoneNumber } = formData;
  
      if (!name || !fatherName || !address || !phoneNumber) {
        Swal.fire("Error", "Please fill out all fields!", "error");
        return;
      }
  
      try {
        // Step 1: Create user with email and password
        const userCredential = await createUserWithEmailAndPassword(
          auth,
          formData.email,
          formData.password
        );
        const user = userCredential.user;
        console.log("User signed up:", user);
  
        // Step 2: Save the form data to Firebase Realtime Database
        const userId = user.uid; // Firebase Auth user ID
        await set(ref(db, "users/" + userId), {
          name,
          fatherName,
          address,
          phoneNumber,
          createdAt: new Date().toISOString(),
        });
  
        Swal.fire("Success", "Registration successful!", "success");
        setFormData({
          name: "",
          fatherName: "",
          address: "",
          phoneNumber: "",
        });
      } catch (error) {
        console.error("Error signing up:", error);
        Swal.fire("Error", "Failed to save data!", "error");
      }
    };
  
    return (
      <div className="flex items-center justify-center min-h-screen bg-gray-100">
        <div className="w-full max-w-lg bg-white rounded-lg shadow-md p-8">
          <h2 className="text-2xl font-bold text-gray-800 text-center mb-6">Registration Form</h2>
          <form onSubmit={handleSubmit} className="space-y-4">
            {/* Name Field */}
            <div>
              <label htmlFor="name" className="block text-sm font-medium text-gray-700">Name</label>
              <input
                type="text"
                id="name"
                name="name"
                className="w-full px-4 py-2 mt-2 border rounded-lg focus:ring-blue-500 focus:border-blue-500"
                placeholder="Enter your name"
                value={formData.name}
                onChange={handleChange}
              />
            </div>
            {/* Father's Name Field */}
            <div>
              <label htmlFor="fatherName" className="block text-sm font-medium text-gray-700">Father's Name</label>
              <input
                type="text"
                id="fatherName"
                name="fatherName"
                className="w-full px-4 py-2 mt-2 border rounded-lg focus:ring-blue-500 focus:border-blue-500"
                placeholder="Enter your father's name"
                value={formData.fatherName}
                onChange={handleChange}
              />
            </div>
            {/* Address Field */}
            <div>
              <label htmlFor="address" className="block text-sm font-medium text-gray-700">Address</label>
              <textarea
                id="address"
                name="address"
                className="w-full px-4 py-2 mt-2 border rounded-lg focus:ring-blue-500 focus:border-blue-500"
                placeholder="Enter your address"
                value={formData.address}
                onChange={handleChange}
              ></textarea>
            </div>
            {/* Phone Number Field */}
            <div>
              <label htmlFor="phoneNumber" className="block text-sm font-medium text-gray-700">Phone Number</label>
              <input
                type="tel"
                id="phoneNumber"
                name="phoneNumber"
                className="w-full px-4 py-2 mt-2 border rounded-lg focus:ring-blue-500 focus:border-blue-500"
                placeholder="Enter your phone number"
                value={formData.phoneNumber}
                onChange={handleChange}
              />
            </div>
            <button
              type="submit"
              className="w-full bg-blue-500 hover:bg-blue-600 text-white py-2 px-4 rounded-lg"
            >
              Submit
            </button>
          </form>
        </div>
      </div>
    );
  };
