import React from 'react';
import { useLocation, useNavigate } from 'react-router-dom';

const Profile = () => {
    const location = useLocation();
    const navigate = useNavigate();
    const user = location.state?.user;
  
    if (!user) {
      navigate('/'); // Redirect to login if no user data
      return null;
    }
  
    return (
      <div className="flex flex-col items-center justify-center min-h-screen bg-gray-100">
        <div className="w-full max-w-md bg-white rounded-lg shadow-md p-6">
          <h2 className="text-2xl font-bold text-gray-800 text-center">Your Profile</h2>
          <div className="mt-4">
            <img
              src={user.photoURL || 'https://via.placeholder.com/150'}
              alt="Profile"
              className="w-32 h-32 rounded-full mx-auto"
            />
            <h3 className="text-xl text-center mt-4">{user.displayName || 'No Name'}</h3>
            <p className="text-center text-gray-600">{user.email}</p>
          </div>
          <button
            onClick={() => navigate('/home')}
            className="mt-6 w-full bg-blue-500 hover:bg-blue-600 text-white py-2 px-4 rounded-lg"
          >
            Go to Home
          </button>
        </div>
      </div>
    );
  };
export default Profile
