import React, { useState } from "react";
db// Adjust the path to your Firebase config
import { collection, query, where, getDocs } from "firebase/firestore";

const ContactUs = () => {
  const [city, setCity] = useState("");
  const [bloodGroup, setBloodGroup] = useState("");
  const [results, setResults] = useState([]);

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!city || !bloodGroup) {
      alert("Please provide both city and blood group.");
      return;
    }

    try {
      const q = query(
        collection(db, "users"), // Change "users" to the name of your Firestore collection
        where("city", "==", city),
        where("bloodGroup", "==", bloodGroup)
      );

      const querySnapshot = await getDocs(q);
      const matchedData = [];

      querySnapshot.forEach((doc) => {
        matchedData.push(doc.data());
      });

      if (matchedData.length > 0) {
        setResults(matchedData);
      } else {
        alert("No matching data found.");
      }
    } catch (error) {
      console.error("Error fetching data: ", error);
      alert("An error occurred while fetching data.");
    }
  };

  return (
    <div className="contact-us-container">
      <h2 className="text-2xl font-bold mb-4">Find Blood Donors or Receivers</h2>
      <form onSubmit={handleSubmit} className="space-y-4">
        <div>
          <label className="block text-gray-700 font-medium">City</label>
          <input
            type="text"
            className="w-full border border-gray-300 rounded-lg px-4 py-2"
            placeholder="Enter your city"
            value={city}
            onChange={(e) => setCity(e.target.value)}
            required
          />
        </div>
        <div>
          <label className="block text-gray-700 font-medium">Blood Group</label>
          <input
            type="text"
            className="w-full border border-gray-300 rounded-lg px-4 py-2"
            placeholder="Enter blood group (e.g., A+, O-)"
            value={bloodGroup}
            onChange={(e) => setBloodGroup(e.target.value)}
            required
          />
        </div>
        <button
          type="submit"
          className="bg-blue-500 text-white px-4 py-2 rounded-lg hover:bg-blue-600"
        >
          Search
        </button>
      </form>

      <div className="mt-6">
        {results.length > 0 ? (
          <div>
            <h3 className="text-lg font-bold">Matching Results:</h3>
            <ul className="mt-4 space-y-2">
              {results.map((result, index) => (
                <li
                  key={index}
                  className="p-4 border border-gray-300 rounded-lg shadow-md"
                >
                  <p>
                    <strong>Name:</strong> {result.name}
                  </p>
                  <p>
                    <strong>City:</strong> {result.city}
                  </p>
                  <p>
                    <strong>Blood Group:</strong> {result.bloodGroup}
                  </p>
                  <p>
                    <strong>Contact:</strong> {result.email}
                  </p>
                </li>
              ))}
            </ul>
          </div>
        ) : (
          <p className="text-gray-600 mt-4">No results to display.</p>
        )}
      </div>
    </div>
  );
};

export default ContactUs;
