import React, { useState, useEffect, useContext } from "react";
import { Helmet } from "react-helmet";
import Mycontext from "../context/Mycontext";

function UpdateProfile() {
  const { currentUser, updateUserProfile } = useContext(Mycontext);
  const [name, setName] = useState(currentUser?.name || "");
  const [email, setEmail] = useState(currentUser?.email || "");
  const [address, setAddress] = useState(currentUser?.address || "");
  const [number, setNumber] = useState(currentUser?.number || "");
  const [url, setUrl] = useState(currentUser?.url || "");
  const [role, setRole] = useState(currentUser?.role || "");

  useEffect(() => {
    if (currentUser) {
      setName(currentUser.name);
      setEmail(currentUser.email);
      setAddress(currentUser.address);
      setNumber(currentUser.number);
      setUrl(currentUser.url);
      setRole(currentUser.role);
    }
  }, [currentUser]);

  const handleSubmit = (e) => {
    e.preventDefault();
    updateUserProfile({ name, email, address, number, url, role });
  };

  return (
    <div className="flex min-h-screen items-center justify-center bg-red-100">
    <div className="w-full max-w-md bg-white rounded-lg shadow-lg p-6">
      <h2 className="text-2xl font-bold text-red-600 text-center">
          <Helmet>
            <title>Update Profile</title>
          </Helmet>
          Update Profile
        </h2>
        <form onSubmit={handleSubmit}>
          <div className="mb-4">
            <label className="block text-gray-700 text-sm font-medium" htmlFor="name">
              Name
            </label>
            <input
              type="text"
              id="name"
              className="w-full px-4 py-2 mt-2 border border-gray-300 rounded-lg"
              placeholder="Enter your name"
              value={name}
              onChange={(e) => setName(e.target.value)}
            />
          </div>
          <div className="mb-4">
            <label className="block text-gray-700 text-sm font-medium" htmlFor="email">
              Email
            </label>
            <input
              type="email"
              id="email"
              className="w-full px-4 py-2 mt-2 border border-gray-300 rounded-lg"
              placeholder="Enter your email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
            />
          </div>
          <div className="mb-4">
            <label className="block text-gray-700 text-sm font-medium" htmlFor="number">
              Number
            </label>
            <input
              type="number"
              id="number"
              className="w-full px-4 py-2 mt-2 border border-gray-300 rounded-lg"
              placeholder="Enter your number"
              value={number}
              onChange={(e) => setNumber(e.target.value)}
            />
          </div>
          <div className="mb-4">
            <label className="block text-gray-700 text-sm font-medium" htmlFor="address">
              Address
            </label>
            <input
              type="text"
              id="address"
              className="w-full px-4 py-2 mt-2 border border-gray-300 rounded-lg"
              placeholder="Enter your address"
              value={address}
              onChange={(e) => setAddress(e.target.value)}
            />
          </div>
          <div className="mb-4">
            <label className="block text-gray-700 text-sm font-medium" htmlFor="url">
              Profile Image URL
            </label>
            <input
              type="url"
              id="url"
              className="w-full px-4 py-2 mt-2 border border-gray-300 rounded-lg"
              placeholder="Enter image URL"
              value={url}
              onChange={(e) => setUrl(e.target.value)}
            />
          </div>
          <div className="mb-4">
            <label className="block text-gray-700 text-sm font-medium" htmlFor="role">
              Role
            </label>
            <select
              id="role"
              className="w-full px-4 py-2 mt-2 border border-gray-300 rounded-lg"
              value={role}
              onChange={(e) => setRole(e.target.value)}
            >
              <option value="Donor">Donor</option>
              <option value="Receiver">Receiver</option>
            </select>
          </div>
          <div className="mb-4">
            <button
              type="submit"
              className="w-full bg-red-500 hover:bg-red-600 text-white py-2 px-4 rounded-lg"
            >
              Update Profile
            </button>
          </div>
        </form>
      </div>
    </div>
  );
}

export default UpdateProfile;
