import React, { useState } from "react";
import { Helmet } from "react-helmet";
import { auth, db } from "../init-firebase";
import { doc, getDoc, updateDoc } from "firebase/firestore";
import { updateEmail, updatePassword, reauthenticateWithCredential, EmailAuthProvider } from "firebase/auth";
import { useNavigate } from "react-router-dom";
import '../styles/updatepr.css'

function UpdateProfile() {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [role, setRole] = useState("");
  const [currentPassword, setCurrentPassword] = useState("");
  const navigate = useNavigate();

  const Submitbtn = async (event) => {
    event.preventDefault();

    const currentUser = auth.currentUser;
    if (!currentUser) {
      alert("No user is logged in.");
      return;
    }

    const userId = currentUser.uid;
    const profileData = { Name: name, UserEmail: email, Role: role };

    try {
      const userDocRef = doc(db, "SignuUser", userId);
      const userDocSnapshot = await getDoc(userDocRef);

      if (!userDocSnapshot.exists()) {
        alert("User document not found!");
        return;
      }
      await updateDoc(userDocRef, profileData);
      alert("Profile updated successfully!");

      // Update email
      if (email && email !== currentUser.email) {
        await updateEmail(currentUser, email);
        alert("Email updated successfully!");
      }

      // Update password
      if (password) {
        if (!currentPassword) {
          alert("Please enter your current password to update your password.");
          return;
        }
        const credential = EmailAuthProvider.credential(
          currentUser.email,
          currentPassword
        );
        await reauthenticateWithCredential(currentUser, credential);
        await updatePassword(currentUser, password);
        alert("Password updated successfully!");
      }

      navigate("/dashboard");
    } catch (error) {
      console.error("Error updating profile:", error);
      alert(error.message || "Failed to update profile. Please try again.");
    }
  };


  return (
    <div>
    <div className="flex min-h-screen items-center justify-center bg-red-100 blood-background">
    <Helmet>
      <title>Update Profile</title>
    </Helmet>
    <div className="w-full max-w-md bg-white rounded-lg shadow-lg p-6 blood-shadow">
      <h2 className="text-2xl font-bold text-red-600 text-center blood-title">Update Profile</h2>
      <form onSubmit={Submitbtn}>
        <div className="mb-4">
          <label className="block text-gray-700 text-sm font-medium" htmlFor="name">Name</label>
          <input
            type="text"
            id="name"
            className="w-full px-4 py-2 mt-2 border border-gray-300 rounded-lg blood-input"
            placeholder="Enter your name"
            value={name}
            onChange={(e) => setName(e.target.value)}
          />
        </div>
        <div className="mb-4">
          <label className="block text-gray-700 text-sm font-medium" htmlFor="email">Email</label>
          <input
            type="email"
            id="email"
            className="w-full px-4 py-2 mt-2 border border-gray-300 rounded-lg blood-input"
            placeholder="Enter your email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
        </div>
        <div className="mb-4">
          <label className="block text-gray-700 text-sm font-medium" htmlFor="currentPassword">Current Password</label>
          <input
            type="password"
            id="currentPassword"
            className="w-full px-4 py-2 mt-2 border border-gray-300 rounded-lg blood-input"
            placeholder="Enter your current password"
            value={currentPassword}
            onChange={(e) => setCurrentPassword(e.target.value)}
          />
        </div>
        <div className="mb-4">
          <label className="block text-gray-700 text-sm font-medium" htmlFor="password">New Password</label>
          <input
            type="password"
            id="password"
            className="w-full px-4 py-2 mt-2 border border-gray-300 rounded-lg blood-input"
            placeholder="Enter your new password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
        </div>
        <div className="mb-4">
          <label className="block text-gray-700 text-sm font-medium" htmlFor="role">Role</label>
          <select
            id="role"
            className="w-full px-4 py-2 mt-2 border border-gray-300 rounded-lg blood-input"
            value={role}
            onChange={(e) => setRole(e.target.value)}
          >
            <option value="Donor">Donor</option>
            <option value="Receiver">Receiver</option>
          </select>
        </div>
        <div className="mb-4">
          <button
            type="submit"
            className="w-full bg-blue-500 hover:bg-blue-600 text-white py-2 px-4 rounded-lg blood-button"
          >
            Update Profile
          </button>
        </div>
      </form>
    </div>
  </div>
  </div>
  );
}

export default UpdateProfile;
