import React, { useState, useEffect,} from "react";
import { Helmet } from "react-helmet";
import { auth } from "../init-firebase";

function UpdateProfile() {
  const [name, setName] = useState(currentUser?.name || "");
  const [email, setEmail] = useState(currentUser?.email || "");
  const [role, setRole] = useState(currentUser?.role || "");
  const [password,setPassword]=useState('')
  const updateUserProfile = async (userId, profileData) => {
    const { name, email, role } = profileData;
  
    try {
      // Firestore update
      const userDocRef = doc(db, "users", userId); // Replace 'users' with your Firestore collection name
      await updateDoc(userDocRef, {
        name: name || "",
        email: email || "",
        role: role || "",
      });
  
      // Optionally update Firebase Authentication profile
      if (auth.currentUser) {
        await auth.currentUser.updateProfile({
          displayName: name || auth.currentUser.displayName,
        });
      }
  
      console.log("Profile updated successfully!");
      return true; // Success
    } catch (error) {
      console.error("Error updating profile:", error);
      return false; // Failure
    }
  };
  const handleSubmit = async (event) => {
    event.preventDefault();
  
    const profileData = {
      name,
      email,
      role,
    };
  
    const userId = auth.currentUser?.uid; // Get the current user's UID
    if (!userId) {
      alert("No user is logged in.");
      return;
    }
  
    const isUpdated = await updateUserProfile(userId, profileData);
    if (isUpdated) {
      alert("Profile updated successfully!");
    } else {
      alert("Failed to update profile. Please try again.");
    }
  };
 

  return (
    <div className="flex min-h-screen items-center justify-center bg-red-100">
    <div className="w-full max-w-md bg-white rounded-lg shadow-lg p-6">
      <h2 className="text-2xl font-bold text-red-600 text-center">
          <Helmet>
            <title>Update Profile</title>
          </Helmet>
          Update Profile
        </h2>
        <form onSubmit={handleSubmit}>
          <div className="mb-4">
            <label className="block text-gray-700 text-sm font-medium" htmlFor="name">
              Name
            </label>
            <input
              type="text"
              id="name"
              className="w-full px-4 py-2 mt-2 border border-gray-300 rounded-lg"
              placeholder="Enter your name"
              value={name}
              onChange={(e) => setName(e.target.value)}
            />
          </div>
          <div className="mb-4">
            <label className="block text-gray-700 text-sm font-medium" htmlFor="email">
              Email
            </label>
            <input
              type="email"
              id="email"
              className="w-full px-4 py-2 mt-2 border border-gray-300 rounded-lg"
              placeholder="Enter your email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
            />
          </div>
          <div className="mb-4">
            <label className="block text-gray-700 text-sm font-medium" htmlFor="email">
              PAssword
            </label>
            <input
              type="password"
              id="password"
              className="w-full px-4 py-2 mt-2 border border-gray-300 rounded-lg"
              placeholder="Enter your email"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
          </div>
         
         
          <div className="mb-4">
            <label className="block text-gray-700 text-sm font-medium" htmlFor="role">
              Role
            </label>
            <select
              id="role"
              className="w-full px-4 py-2 mt-2 border border-gray-300 rounded-lg"
              value={role}
              onChange={(e) => setRole(e.target.value)}
            >
              <option value="Donor">Donor</option>
              <option value="Receiver">Receiver</option>
            </select>
          </div>
          <div className="mb-4">
            <button
             onClick={updateUserProfile}
              className="w-full bg-blue-500 hover:bg-blue-600 text-white py-2 px-4 rounded-lg"
            >
              Update Profile
            </button>
          </div>
        </form>
      </div>
    </div>
  );
}

export default UpdateProfile;
