import React, { useState } from "react";
import { collection, addDoc, getDocs, query, where } from "firebase/firestore";
import { auth, db } from "../init-firebase";
import Swal from "sweetalert2";
import { Helmet } from "react-helmet";
import "../styles/home.css";
import { signOut } from "firebase/auth";

const HomePage = () => {
  const [bloodGroup, setBloodGroup] = useState("");
  const [city, setCity] = useState("");
  const [name, setName] = useState("");
  const [age, setAge] = useState("");
  const [ContactNo, setContactNo] = useState("");
  const [donors, setDonors] = useState([]);

  // Function to add a donor to Firestore
  const DonateButton = async () => {
    if (!name || !bloodGroup || !city || !ContactNo) {
      Swal.fire("Please fill in all the fields before donating blood.");
      return;
    }

    try {
      await addDoc(collection(db, "BloodDoners"), {
        DonerName: name,
        DonerBloodGroup: bloodGroup,
        CityName: city,
        ContactNo: ContactNo,
      });

      Swal.fire(`Thank you ${name} for your blood donation!`);
      setName("");
      setBloodGroup("");
      setCity("");
      setContactNo("");
    } catch (error) {
      console.error("Error donating blood:", error.message);
      Swal.fire("An error occurred while donating blood. Please try again.");
    }
  };

  // Function to find donors based on blood group and city
  const FindDoner = async () => {
    if (!bloodGroup || !city) {
      Swal.fire("Please add both city and blood group");
      return;
    }

    try {
      const filters = [];
      if (bloodGroup) {
        filters.push(where("DonerBloodGroup", "==", bloodGroup));
      }
      if (city) {
        filters.push(where("CityName", "==", city));
      }

      const matchData = query(collection(db, "BloodDoners"), ...filters);
      const querySnapshot = await getDocs(matchData);

      const matchedDonors = querySnapshot.docs.map((doc) => ({
        id: doc.id,
        DonerName: doc.data().DonerName,
        DonerBloodGroup: doc.data().DonerBloodGroup,
        CityName: doc.data().CityName,
        ContactNo: doc.data().ContactNo,
      }));

      if (matchedDonors.length === 0) {
        Swal.fire("No donors found matching your criteria.");
      } else {
        setDonors(matchedDonors);
      }
    } catch (error) {
      console.error("Error fetching donors:", error);
      Swal.fire("Error", "Failed to fetch data", "error");
    }
  };

  // Logout Function
  const LogOut = async () => {
    try {
      await signOut(auth);
      Swal.fire("Success", "You have been logged out.", "success");
      window.location.href = "/login";
    } catch (error) {
      console.error("Error during logout:", error);
      Swal.fire("Error", "Failed to log out. Please try again.", "error");
    }
  };

  return (
    <div className="homepage">
      <Helmet>
        <title>HomePage</title>
      </Helmet>
      <div className="navbar">
        <div className="navbar-brand">Blood Bank</div>
        <div className="navbar-toggle" onClick="toggleNavbar()">
          <span></span>
          <span></span>
          <span></span>
        </div>
        <div className="navbar-links">
          <a href="/home">Home</a>
          <a href="/profile">Profile</a>
          <button onClick={LogOut}>Logout</button>
          <a href="/contact">Contact Us</a>
        </div>
      </div>

      <h1 className="title">Blood Bank</h1>
      <div className="form-container">
        <input
          type="text" placeholder="Enter Your Name" value={name} onChange={(e) => setName(e.target.value)}  />
        <input type="number"  placeholder="Enter Your Age" value={age} onChange={(e) => setAge(e.target.value)} />
        <input  type="text" placeholder="Enter Blood Group" value={bloodGroup} onChange={(e) => setBloodGroup(e.target.value)} />
        <input  type="text" placeholder="Enter City" value={city} onChange={(e) => setCity(e.target.value)} />
        <input type="text" placeholder="Enter Contact NO"value={ContactNo}onChange={(e) => setContactNo(e.target.value)} />
          <div className="button-group">
          <button onClick={DonateButton} className="btn donate-btn">
            Donate Blood
          </button>
          <button onClick={FindDoner} className="btn find-btn">
            Find Donor
          </button>
        </div>
      </div>

      <div className="donor-list">
        <h2>Donor List</h2>
        {donors.length > 0 ? (
          donors.map((donor, index) => (
            <div key={index} className="donor-card">
              <p>
                <strong>Name:</strong> {donor.DonerName}
              </p>
              <p>
                <strong>Blood Group:</strong> {donor.DonerBloodGroup}
              </p>
              <p>
                <strong>City:</strong> {donor.CityName}
              </p>
              <p>
                <strong>Contact No:</strong> {donor.ContactNo}
              </p>
            </div>
          ))
        ) : (
          <p>No donors found</p>
        )}
      </div>
    </div>
  );
};

export default HomePage;
