import React, { useContext, useState } from "react";
import { Link,} from "react-router-dom";
import { Helmet } from "react-helmet";
import Mycontext from "../context/Mycontext";
function Signup() {
         const { SignupFirebase } = useContext(Mycontext);
         const [name, setName] = useState('');
         const [email, setEmail] = useState('');
         const [password, setPassword] = useState('');
         const [doners, SetDoner] = useState('');
           const SignupButton =() => {
       SignupFirebase(name, email, password,doners);
    };

  return (
    <div className="flex min-h-screen items-center justify-center bg-gradient-to-br from-red-200 via-white to-red-300">
      <Helmet>
        <title>Sign In</title>
      </Helmet>
      <div className="w-full max-w-md bg-white rounded-lg shadow-xl p-8 relative overflow-hidden">
        {/* Animated Background Effect */}
        <div className="absolute inset-0 bg-red-600 opacity-10 blur-3xl transform scale-150 rounded-full animate-pulse"></div>

        <h2 className="text-3xl font-bold text-red-600 text-center mb-6">
          Welcome Back
        </h2>

        {/* Form */}
        <form
          className="mt-6"
          onSubmit={SignupFirebase}
        >
          {/* Email Input */}
          <div className="mb-4">
            <label className="block text-gray-700 text-sm font-medium" htmlFor="email">
              Email
            </label>
            <input
              type="email"
              id="email"
              className="w-full px-4 py-2 mt-2 border border-gray-300 rounded-lg focus:ring-red-500 focus:border-red-500"
              placeholder="Enter your email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
            />
          </div>

          {/* Password Input */}
          <div className="mb-4">
            <label className="block text-gray-700 text-sm font-medium" htmlFor="password">
              Password
            </label>
            <input
              type="password"
              id="password"
              className="w-full px-4 py-2 mt-2 border border-gray-300 rounded-lg focus:ring-red-500 focus:border-red-500"
              placeholder="Enter your password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
          </div>

          {/* Sign In Button */}
          <button
            type="submit"
            className="w-full bg-red-500 hover:bg-red-600 text-white py-2 px-4 rounded-lg transform transition-transform hover:scale-105"
          >
            Sign In
          </button>

          {/* Sign Up Link */}
          <p className="text-sm text-center text-gray-600 mt-4">
            Don't have an account?{" "}
            <Link to="/" className="text-red-500 hover:underline">
              Sign Up
            </Link>
          </p>
        </form>
      </div>
    </div>
  );
}

export default Signup;
