import React, { useContext, useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import { Helmet } from "react-helmet";
import Mycontext from "../context/Mycontext";

const SignIn =()=> {
  const { SignInfirebase } = useContext(Mycontext);
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const Navigate=useNavigate()
  const SiginFire=()=>{
   SignInfirebase(email,password,)
  Navigate()
   
   }
  return (
    <div className="flex min-h-screen items-center justify-center bg-gray-100">
      <div className="w-full max-w-md bg-white rounded-lg shadow-md p-6">
        <h2 className="text-2xl font-bold text-gray-800 text-center">
           <Helmet>Sign In</Helmet> </h2>
        <form className="mt-6">
          <div className="mb-4">
            <label className="block text-gray-700 text-sm font-medium" htmlFor="email">Email</label>
            <input  type="email" id="email"className="w-full px-4 py-2 mt-2 border border-gray-300 rounded-lg focus:ring-blue-500 focus:border-blue-500"
              placeholder="Enter your email" value={email}onChange={(e) => setEmail(e.target.value)}
            />
          </div>
          <div className="mb-4">
            <label className="block text-gray-700 text-sm font-medium" htmlFor="password">Password</label>
            <input type="password" id="password" className="w-full px-4 py-2 mt-2 border border-gray-300 rounded-lg focus:ring-blue-500 focus:border-blue-500"
             placeholder="Enter your password"  value={password} onChange={(e) => setPassword(e.target.value)}
            />
          </div>
          <button
            className="w-full bg-green-500 hover:bg-green-600 text-white py-2 px-4 rounded-lg"
            onClick={ SiginFire}  >
            Sign In
          </button>
          <p className="text-sm text-center text-gray-600">
            Don't have an account?{' '}
            <Link to="/" className="text-green-500 hover:underline">
              Sign Up
            </Link>
          </p>
        </form>
      </div>
    </div>
  );
};

export default SignIn;
