import React, { useState, useEffect } from "react";
import { collection, addDoc, query, where, getDocs, doc, setDoc } from "firebase/firestore";
import { auth, db } from "../init-firebase";
import Swal from "sweetalert2";
import { onAuthStateChanged, signOut } from "firebase/auth";
import { Helmet } from "react-helmet";
import "../styles/home.css";

const HomePage = () => {
  const [bloodGroup, setBloodGroup] = useState("");
  const [name, setName] = useState("");
  const [age, setAge] = useState("");
  const [contactNo, setContactNo] = useState("");

  const DonateBlood = async () => {
    onAuthStateChanged(auth, async (user) => {
      if (user) {
        const currentUserEmail = user.email.trim().toLowerCase();
        console.log("Logged-in user email:", currentUserEmail);

        try {
          // Check if the user has already donated
          const donationsRef = collection(db, "Donations");
          const donationQuery = query(donationsRef, where("UserEmail", "==", currentUserEmail));
          const donationSnapshot = await getDocs(donationQuery);

          if (!donationSnapshot.empty) {
            // User has already donated
            Swal.fire("You have already donated blood!", "You can only donate once per account.", "info");
            return;
          }

          // Add a new donation record to the "Donations" collection
          await addDoc(collection(db, "Donations"), {
            Name: name,
            Age: age,
            ContactNo: contactNo,
            BloodGroup: bloodGroup,
            UserEmail: currentUserEmail,
            donationDate: new Date(),
          });

          Swal.fire("Blood donation recorded successfully!", "", "success");

          // Optionally, reset the form after submission
          setName("");
          setAge("");
          setContactNo("");
          setBloodGroup("");

        } catch (error) {
          console.error("Error saving donation:", error.message);
          Swal.fire("An error occurred. Please try again.", "", "error");
        }
      } else {
        console.log("No user logged in.");
        Swal.fire("Please log in to proceed!", "", "info");
      }
    });
  };

  // LogOut Function
  const LogOut = async () => {
    try {
      await signOut(auth);
      Swal.fire("Success", "You have been logged out.", "success");
      window.location.href = "/login";
    } catch (error) {
      console.error("Error during logout:", error);
      Swal.fire("Error", "Failed to log out. Please try again.", "error");
    }
  };

  return (
    <div className="homepage">
      <Helmet>
        <title>HomePage</title>
      </Helmet>
      <div className="navbar">
        <div className="navbar-brand">Blood Bank</div>
        <div className="navbar-links">
          <a href="/home">Home</a>
          <a href="/profile">Profile</a>
          <a href="/doner">Donors</a>
          <button onClick={LogOut}>Logout</button>
        </div>
      </div>

      <h1 className="title">Blood Bank</h1>
      <div className="form-container">
        <input
          type="text"
          placeholder="Name"
          value={name}
          onChange={(e) => setName(e.target.value)}
        />
        <input
          type="number"
          placeholder="Age"
          value={age}
          onChange={(e) => setAge(e.target.value)}
        />
        <input
          type="text"
          placeholder="Contact No"
          value={contactNo}
          onChange={(e) => setContactNo(e.target.value)}
        />
        <input
          type="text"
          placeholder="Enter Blood Group"
          value={bloodGroup}
          onChange={(e) => setBloodGroup(e.target.value)}
        />
        <div className="button-group">
          <button onClick={DonateBlood} className="btn donate-btn">
            Donate Blood
          </button>
        </div>
      </div>

      <footer className="footer">
        <div className="footer-container">
          <p>&copy; {new Date().getFullYear()} Blood Bank | All Rights Reserved</p>
        </div>
      </footer>
    </div>
  );
};

export default HomePage;
