import React, { useState } from "react";
import { collection, addDoc, getDocs, query, where } from "firebase/firestore";
import { auth, db } from "../init-firebase";
import Swal from "sweetalert2";
import { Helmet } from "react-helmet";
import Footer from './Footer'
import { useNavigate } from "react-router-dom";
import NavbaR from "./navbar";
import '../styles/bloodsec.css'


const HomePage = () => {
  const [formData, setFormData] = useState({ name: '', phone: '', bloodType: '', city: '' });
  const [submitted, setSubmitted] = useState(false);

  const handleSubmit = (e) => {
    e.preventDefault();
    // Handle form submission logic here
    setSubmitted(true);
  };     

        const [bloodGroup, setBloodGroup] = useState("");
        
        const [isNavbarOpen, setIsNavbarOpen] = useState(false);
        const Navigate=useNavigate()
        const DonateButton = async () => {
         
          
    const user = auth.currentUser;
    if (!user) {
      Swal.fire("Please log in to donate blood.");
      return;
    }
  
    try {
      const q = query(collection(db, "BloodDonors"),
        where("Email", "==", user.email)
      );
      const donorSnapshot = await getDocs(q);
  
      if (!donorSnapshot.empty) {
        Swal.fire("You have already donated blood once.");
        return;
      }
  
      await addDoc(collection(db, "BloodDonors"), {
       
        DonerBloodGroup: bloodGroup,
       
        Email: user.email,
        DonationDate: new Date()
      });
    //  console.log(donorSnapshot)
      
      setBloodGroup("");
     
    } catch (error) {
      console.error("Error donating blood:", error.message); // Log the detailed error message
      Swal.fire("An error occurred while donating blood. Please try again.");
    }
  };
  
  
  
  const toggleNavbar = () => {
    setIsNavbarOpen(!isNavbarOpen);
  };
  return (
    <div><NavbaR/>
    <div className="homepage">
      <Helmet>
        <title>HomePage</title>
      </Helmet>
      
     <h1 className="title">Blood Bank</h1>
      <div className="form-container">
        
        <input
          type="text"
          placeholder="Enter Blood Group"
          value={bloodGroup}
          onChange={(e) => setBloodGroup(e.target.value)}
        />
       
       
        <div className="button-group">
          <button onClick={DonateButton} className="btn donate-btn">
            Donate Blood
          </button>
        </div>

         </div>

         <section className="bg-red-100 py-16">
      <div className="container mx-auto px-4">
        <h2 className="text-4xl font-semibold text-center text-red-600 mb-6">Why Donate Blood?</h2>
        <p className="text-lg text-gray-700 text-center mb-8">
          Donating blood is one of the most selfless acts a person can do. Every donation helps save lives.
          It's quick, easy, and safe, and it can make a huge difference for someone in need. When you donate blood, you are
          helping to ensure that hospitals and clinics have enough resources to save lives.
        </p>
        <div className="flex justify-center gap-6">
          <div className="bg-white p-6 rounded-lg shadow-md max-w-sm">
            <h3 className="text-2xl text-red-600 font-semibold mb-4">Save Lives</h3>
            <p className="text-gray-600">Your donation can help someone in a life-threatening situation such as a car accident or surgery.</p>
          </div>
          <div className="bg-white p-6 rounded-lg shadow-md max-w-sm">
            <h3 className="text-2xl text-red-600 font-semibold mb-4">Easy Process</h3>
            <p className="text-gray-600">Blood donation is a simple process that only takes around 30 minutes, but the impact lasts a lifetime.</p>
          </div>
          <div className="bg-white p-6 rounded-lg shadow-md max-w-sm">
            <h3 className="text-2xl text-red-600 font-semibold mb-4">Help Your Community</h3>
            <p className="text-gray-600">Be a hero to someone in need. Blood donation is a community effort to help save lives.</p>
          </div>
        </div>
      </div>
    </section>


    <section className="py-16 bg-white">
      <div className="container mx-auto px-4">
        <h2 className="text-4xl font-semibold text-center text-red-600 mb-6">Request Blood</h2>
        {submitted ? (
          <p className="text-lg text-center text-green-600 mb-6">Your request has been submitted successfully!</p>
        ) : (
          <form onSubmit={handleSubmit} className="max-w-3xl mx-auto">
            <div className="grid grid-cols-1 sm:grid-cols-2 gap-6">
              <div>
                <label className="block text-sm font-bold text-gray-700 mb-2">Full Name</label>
                <input
                  type="text"
                  className="w-full border rounded-lg px-4 py-2"
                  value={formData.name}
                  onChange={(e) => setFormData({ ...formData, name: e.target.value })}
                  required
                />
              </div>
              <div>
                <label className="block text-sm font-bold text-gray-700 mb-2">Phone Number</label>
                <input
                  type="text"
                  className="w-full border rounded-lg px-4 py-2"
                  value={formData.phone}
                  onChange={(e) => setFormData({ ...formData, phone: e.target.value })}
                  required
                />
              </div>
              <div>
                <label className="block text-sm font-bold text-gray-700 mb-2">Blood Type</label>
                <select
                  className="w-full border rounded-lg px-4 py-2"
                  value={formData.bloodType}
                  onChange={(e) => setFormData({ ...formData, bloodType: e.target.value })}
                  required
                >
                  <option value="">Select Blood Type</option>
                  <option value="A+">A+</option>
                  <option value="B+">B+</option>
                  <option value="O+">O+</option>
                  <option value="AB+">AB+</option>
                  <option value="A-">A-</option>
                  <option value="B-">B-</option>
                  <option value="O-">O-</option>
                  <option value="AB-">AB-</option>
                </select>
              </div>
              <div>
                <label className="block text-sm font-bold text-gray-700 mb-2">City</label>
                <input
                  type="text"
                  className="w-full border rounded-lg px-4 py-2"
                  value={formData.city}
                  onChange={(e) => setFormData({ ...formData, city: e.target.value })}
                  required
                />
              </div>
            </div>
            <button
              type="submit"
              className="w-full bg-red-600 text-white text-lg py-3 rounded-lg mt-6 hover:bg-red-700 transition"
            >
              Submit Request
            </button>
          </form>
        )}
      </div>
    </section>
       <Footer/>
    </div>
    </div>
  );
};

export default HomePage;
