import React, { useState, useEffect } from "react";
import { collection, addDoc, doc, getDoc } from "firebase/firestore";
import { auth, db } from "../init-firebase";
import Swal from "sweetalert2";
import { onAuthStateChanged, signOut } from "firebase/auth";
import { Helmet } from "react-helmet";
import "../styles/home.css";

const HomePage = () => {
  const [bloodGroup, setBloodGroup] = useState("");
  const [userData, setUserData] = useState(null);

  // Fetch logged-in user's data
  useEffect(() => {
    onAuthStateChanged(auth, async (user) => {
      if (user) {
        const currentUserEmail = user.email.trim().toLowerCase();

        try {
          const userDoc = await getDoc(doc(db, "SinupUser", currentUserEmail));
          if (userDoc.exists()) {
            setUserData(userDoc.data());
          } else {
            Swal.fire(
              "Error",
              "User data not found in the database. Please contact support.",
              "error"
            );
          }
        } catch (error) {
          console.error("Error fetching user data:", error.message);
          Swal.fire("Error", "Unable to fetch user data. Please try again.", "error");
        }
      } else {
        Swal.fire("Please log in to proceed!", "", "info");
        
      }
    });
  }, []);

  // Handle Blood Donation
  const DonateBlood = async () => {
    if (!userData) {
      Swal.fire("Error", "User data not loaded. Please refresh the page.", "error");
      return;
    }

    if (!bloodGroup) {
      Swal.fire("Error", "Please enter your blood group.", "warning");
      return;
    }

    try {
      // Add a new donation record to the "BloodDonors" collection
      await addDoc(collection(db, "BloodDonors"), {
        Name: userData.Name,
        Age: userData.Age,
        ContactNo: userData.ContactNo,
        BloodGroup: bloodGroup,
        UserEmail: userData.Email, // Optional, if Email is in userData
        donationDate: new Date(),
      });

      Swal.fire("Blood donation recorded successfully!", "", "success");

      // Reset the blood group input
      setBloodGroup("");
    } catch (error) {
      console.error("Error saving donation:", error.message);
      Swal.fire("Error", "An error occurred while saving donation. Please try again.", "error");
    }
  };

  // LogOut Function
  const LogOut = async () => {
    try {
      await signOut(auth);
      Swal.fire("Success", "You have been logged out.", "success");
      window.location.href = "/login";
    } catch (error) {
      console.error("Error during logout:", error);
      Swal.fire("Error", "Failed to log out. Please try again.", "error");
    }
  };


  return (
    <div className="homepage">
      <Helmet>
        <title>HomePage</title>
      </Helmet>
      <div className="navbar">
        <div className="navbar-brand">Blood Bank</div>
        <div className="navbar-links">
          <a href="/home">Home</a>
          <a href="/profile">Profile</a>
          <a href="/donor">Donors</a>
          <button onClick={LogOut}>Logout</button>
        </div>
      </div>

      <h1 className="title">Blood Bank</h1>
      <div className="form-container">
        <input
          type="text"
          placeholder="Enter Blood Group"
          value={bloodGroup}
          onChange={(e) => setBloodGroup(e.target.value)}
        />
        <div className="button-group">
          <button onClick={DonateBlood} className="btn donate-btn">
            Donate Blood
          </button>
        </div>
      </div>

      <footer className="footer">
        <div className="footer-container">
          <p>&copy; {new Date().getFullYear()} Blood Bank | All Rights Reserved</p>
        </div>
      </footer>
    </div>
  );
};

export default HomePage;
