import React, { useState, useEffect,} from "react";
import { Helmet } from "react-helmet";
import { auth, db } from "../init-firebase";
import { doc } from "firebase/firestore";

function UpdateProfile() {
  const updateUserProfile = async (userId, profileData) => {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [role, setRole] = useState("");

  const handleSubmit = async (event) => {
    event.preventDefault();

    const currentUser = auth.currentUser;
    if (!currentUser) {
      alert("No user is logged in.");
      return;
    }

    const userId = currentUser.uid; // Get the current user's UID
    const profileData = { name, email, role };

    try {
      const userDocRef = doc(db, "users", userId); // Path to Firestore document
      await updateDoc(userDocRef, profileData);
      alert("Profile updated successfully!");
    } catch (error) {
      console.error("Error updating profile:", error);
      alert("Failed to update profile. Please try again.");
    }
  };
  }
  return (
    <div className="flex min-h-screen items-center justify-center bg-red-100">
    <div className="w-full max-w-md bg-white rounded-lg shadow-lg p-6">
      <h2 className="text-2xl font-bold text-red-600 text-center">
          <Helmet>
            <title>Update Profile</title>
          </Helmet>
          Update Profile
        </h2>
        <form onSubmit={handleSubmit}>
          <div className="mb-4">
            <label className="block text-gray-700 text-sm font-medium" htmlFor="name">
              Name
            </label>
            <input
              type="text"
              id="name"
              className="w-full px-4 py-2 mt-2 border border-gray-300 rounded-lg"
              placeholder="Enter your name"
              value={name}
              onChange={(e) => setName(e.target.value)}
            />
          </div>
          <div className="mb-4">
            <label className="block text-gray-700 text-sm font-medium" htmlFor="email">
              Email
            </label>
            <input
              type="email"
              id="email"
              className="w-full px-4 py-2 mt-2 border border-gray-300 rounded-lg"
              placeholder="Enter your email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
            />
          </div>
          <div className="mb-4">
            <label className="block text-gray-700 text-sm font-medium" htmlFor="email">
              PAssword
            </label>
            <input
              type="password"
              id="password"
              className="w-full px-4 py-2 mt-2 border border-gray-300 rounded-lg"
              placeholder="Enter your email"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
          </div>
         
         
          <div className="mb-4">
            <label className="block text-gray-700 text-sm font-medium" htmlFor="role">
              Role
            </label>
            <select
              id="role"
              className="w-full px-4 py-2 mt-2 border border-gray-300 rounded-lg"
              value={role}
              onChange={(e) => setRole(e.target.value)}
            >
              <option value="Donor">Donor</option>
              <option value="Receiver">Receiver</option>
            </select>
          </div>
          <div className="mb-4">
            <button
             onClick={updateUserProfile}
              className="w-full bg-blue-500 hover:bg-blue-600 text-white py-2 px-4 rounded-lg"
            >
              Update Profile
            </button>
          </div>
        </form>
      </div>
    </div>
  );
}

export default UpdateProfile;
