import React, { useState } from "react";
import { collection, query, where, getDocs } from "firebase/firestore";
import { db } from "../init-firebase";
import Swal from "sweetalert2";
import '../styles/home.css';
import '../styles/cards.css'
import { Helmet } from "react-helmet";

                const DonersData = () => {
                 const [city, setCity] = useState("");
                 const [bloodGroup, setBloodGroup] = useState("");
                 const [doners, setDonors] = useState([]);
                 const DonorCards = () => {
                  const donors = [
                    { name: "John Doe", bloodGroup: "O+", location: "New York", contact: "123-456-7890" },
                    { name: "Jane Smith", bloodGroup: "A-", location: "London", contact: "987-654-3210" },
                    { name: "Ali Khan", bloodGroup: "B+", location: "Karachi", contact: "555-444-3333" },
                    { name: "Maria Lopez", bloodGroup: "AB-", location: "Madrid", contact: "666-777-8888" },
                  ];
                }                

                const DonerBtn = async (e) => {
                e.preventDefault();
                   if (!bloodGroup) {
                  Swal.fire("Please add both city and blood group");
                  return;
                  }

                    try {
                  const SerachBloodDoner = [];
                      if (bloodGroup) {
                        SerachBloodDoner.push(where("DonerBloodGroup", "==", bloodGroup))}
                  if (city) {
                    SerachBloodDoner.push(where("CityName", "==", city));
                  }
            
          const matchData = query(collection(db, "BloodDonors"), ...SerachBloodDoner);
           const querySnapshot = await getDocs(matchData);
      
           const matchedDonors = querySnapshot.docs.map((doc) => ({
             id: doc.id,
             DonerName: doc.data().DonerName,
             DonerBloodGroup: doc.data().DonerBloodGroup,
             CityName: doc.data().CityName,
             ContactNo: doc.data().ContactNo, 
            
                }));
           if (matchedDonors.length === 0) {
             Swal.fire("No doner Found on this BloodGroup and cityName.");
           } else {
             setDonors(matchedDonors);
           }
    } catch (error) {
           console.error("Somethin went  wrong :", error);
      Swal.fire("Error", "Firebase Error", "error");
    }
  };

  return (
      <div className="contact-us-container">
        <Helmet><title>Doner PAge</title></Helmet>
      <h2 className="text-2xl font-bold mb-4 text-center">Find Blood Donors</h2>
      <form onSubmit={DonerBtn} className="space-y-4 max-w-lg mx-auto">
          <div>
          <label className="block text-gray-700 font-medium">City</label>
          <input  type="text" className="w-full border border-gray-300 rounded-lg px-4 py-2 text-sm"
            placeholder="Enter your city" value={city}onChange={(e) => setCity(e.target.value)} required  />
           </div>
           <div> <label className="block text-gray-700 font-medium">Blood Group</label>
          <input type="text" className="w-full border border-gray-300 rounded-lg px-4 py-2 text-sm"
           placeholder="Enter blood group (e.g., A+, O-)" value={bloodGroup} onChange={(e) => setBloodGroup(e.target.value)} required />
        </div>
        <button type="submit" className="bg-blue-500 text-white px-4 py-2 rounded-lg hover:bg-blue-600 w-full" > Find Doner 
        </button>
      </form>

      



    <div className="bg-red-100 py-8">
      <h2 className="text-3xl font-bold text-center text-red-600 mb-6">
        Our Dedicated Donors
      </h2>
      <div className="grid gap-6 px-6 sm:grid-cols-1 md:grid-cols-2 lg:grid-cols-4 max-w-screen-xl mx-auto">
        {Donors.map((donor, index) => (
          <div
            key={index}
            className="bg-white rounded-lg shadow-lg p-6 transform transition-transform duration-300 hover:-translate-y-2 hover:shadow-2xl"
          >
            <h3 className="text-xl font-bold text-gray-800 mb-2">{donor.name}</h3>
            <p className="text-sm text-gray-600 mb-1">
              <strong>Blood Group:</strong> {donor.bloodGroup}
            </p>
            <p className="text-sm text-gray-600 mb-1">
              <strong>Location:</strong> {donor.location}
            </p>
            <p className="text-sm text-gray-600">
              <strong>Contact:</strong> {donor.contact}
            </p>
            <button className="mt-4 px-4 py-2 bg-red-600 text-white rounded-lg hover:bg-red-700">
              View Profile
            </button>
          </div>
        ))}
      </div>
    </div>


      <footer className="footer bg-red-600 text-white py-6">
  <div className="footer-container mx-auto px-6 max-w-screen-xl">
    <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
      {/* About Section */}
      <div>
        <h3 className="text-lg font-bold mb-2">About Blood Bank</h3>
        <p className="text-sm">
          We are committed to saving lives by connecting donors and recipients.
          Our platform ensures safe, easy, and effective blood donations.
        </p>
      </div>

      {/* Quick Links Section */}
      <div>
        <h3 className="text-lg font-bold mb-2">Quick Links</h3>
        <ul className="text-sm space-y-1">
          <li>
            <a href="/donate" className="hover:underline">
              Donate Blood
            </a>
          </li>
          <li>
            <a href="/find-blood" className="hover:underline">
              Find Blood
            </a>
          </li>
          <li>
            <a href="/contact" className="hover:underline">
              Contact Us
            </a>
          </li>
        </ul>
      </div>

      {/* Resources and Info */}
      <div>
        <h3 className="text-lg font-bold mb-2">Resources</h3>
        <ul className="text-sm space-y-1">
          <li>
            <a href="https://www.who.int/health-topics/blood-transfusion" target="_blank" rel="noopener noreferrer" className="hover:underline">
              WHO on Blood Donation
            </a>
          </li>
          <li>
            <a href="https://www.redcrossblood.org/" target="_blank" rel="noopener noreferrer" className="hover:underline">
              Red Cross Blood Services
            </a>
          </li>
          <li>
            <a href="https://www.nhsbt.nhs.uk/" target="_blank" rel="noopener noreferrer" className="hover:underline">
              NHS Blood and Transplant
            </a>
          </li>
        </ul>
      </div>
    </div>

    {/* Footer Bottom */}
    <div className="text-center mt-6 border-t border-gray-400 pt-4">
      <p>&copy; {new Date().getFullYear()} Blood Bank | All Rights Reserved</p>
      <p className="text-xs mt-2">Empowering life through blood donations.</p>
    </div>
  </div>
</footer>

    </div>
  );
};

export default DonersData;
