import React, { useState} from "react";
import { collection, addDoc, getDocs, query, where } from "firebase/firestore";
import { auth, db } from "../init-firebase";
import Swal from "sweetalert2";
import { onAuthStateChanged, signOut } from "firebase/auth";
import { Helmet } from "react-helmet";
const HomePage = () => {
  const [bloodGroup, setBloodGroup] = useState("");
  const [city, setCity] = useState("");
  const [name, setName] = useState("");
  const [age, setAge] = useState("");
  const [ContactNo, setContactNo] = useState("");
  const [donors, setDonors] = useState([]);

  const AddDonate = async (donateGroups, name, city, contactNo) => {
    onAuthStateChanged(auth, async (user) => {
      if (user) {
        const currentProfileUser = user.email;

        const querySnapshot = await getDocs(collection(db, "users"));
        querySnapshot.forEach(async (doc) => {
          const userData = doc.data();

          if (currentProfileUser === userData.Email) {
            // Check if the user has already donated
            const q = query(collection(db, "Donors"), where("Email", "==", userData.Email));
            const donorSnapshot = await getDocs(q);

            if (donorSnapshot.empty) {
              if (donateGroups === "" || donateGroups === "-- Select Blood Group --") {
                Swal.fire("Please Select Your Blood Group !");
              } else if (name === "" || city === "" || contactNo === "") {
                Swal.fire("Please fill in all fields before donating.");
              } else {
                try {
                  const docRef = await addDoc(collection(db, "Donors"), {
                    Name: userData.Name,
                    Email: userData.Email,
                    PhoneNo: userData.PhoneNo,
                    Age: userData.Age,
                    UserType: userData.UserType,
                    City: city,
                    ImageUrl: userData.ImageUrl,
                    BloodGroup: donateGroups,
                  });
                  console.log("Document written with ID: ", docRef.id);
                  Swal.fire(`Thank you ${userData.Name} for your blood donation!`);
                } catch (e) {
                  console.error("Error adding document: ", e);
                }
              }
            } else {
              Swal.fire("You're Already a Donor!");
            }
          }
        });
      } else {
        console.log("User Not Logged In, Please Log In!");
      }
    });
  };

  const DonateButton = async () => {
    if (!name || !bloodGroup || !city || !ContactNo) {
      Swal.fire("Please fill in all the fields before donating blood.");
      return;
    }
  
    // Check if user is authenticated
    const user = auth.currentUser;
    if (!user) {
      Swal.fire("Please log in to donate blood.");
      return;
    }
  
    try {
      // Check if user has already donated
      const donorQuery = query(
        collection(db, "BloodDoners"), 
        where("Email", "==", user.email)
      );
      const donorSnapshot = await getDocs(donorQuery);
  
      if (!donorSnapshot.empty) {
        Swal.fire("You have already donated blood once.");
        return;
      }
  
      // Add new donation record
      await addDoc(collection(db, "BloodDoners"), {
        DonerName: name,
        DonerBloodGroup: bloodGroup,
        CityName: city,
        ContactNo: ContactNo,
        Email: user.email, // Add email to track unique donors
        DonationDate: new Date().toISOString()
      });
  
      Swal.fire(`Thank you ${name} for your blood donation!`);
      setName("");
      setBloodGroup("");
      setCity("");
      setContactNo("");
    } catch (error) {
      console.error("Error donating blood:", error.message);
      Swal.fire("An error occurred while donating blood. Please try again.");
    }
  };
  
 
  // LogOut Function
  const LogOut = async () => {
    try {
      await signOut(auth);
      Swal.fire("Success", "You have been logged out.", "success");
      window.location.href = "/login";
    } catch (error) {
      console.error("Error during logout:", error);
      Swal.fire("Error", "Failed to log out. Please try again.", "error");
    }
  };

  return (
 <div className="homepage">
     <Helmet>         <title>HomePage</title>
      </Helmet>
    <div className="navbar">
         <div className="navbar-brand">Blood Bank</div>
         <div className="navbar-toggle" onClick="toggleNavbar()">
           <span></span>
          <span></span>
           <span></span>
         </div>
         <div className="navbar-links">
          <a href="/home">Home</a>
           <a href="/profile">Profile</a>           <button onClick={LogOut}>Logout</button>
         <a href="/doneryyy">Doner's</a>
         </div>
       </div>

       <h1 className="title">Blood Bank</h1>
       <div className="form-container">
         <input
           type="text" placeholder="Enter Your Name" value={name} onChange={(e) => setName(e.target.value)}  />
         <input type="number"  placeholder="Enter Your Age" value={age} onChange={(e) => setAge(e.target.value)} />
         <input  type="text" placeholder="Enter Blood Group" value={bloodGroup} onChange={(e) => setBloodGroup(e.target.value)} />
         <input  type="text" placeholder="Enter City" value={city} onChange={(e) => setCity(e.target.value)} />         <input type="text" placeholder="Enter Contact NO"value={ContactNo}onChange={(e) => setContactNo(e.target.value)} />
           <div className="button-group">
           <button onClick={DonateButton} className="btn donate-btn">
             Donate Blood
           </button>
           <button onClick={FindDoner} className="btn find-btn">
            Find Donor
           </button>
         </div>
       </div>

       <div className="donor-list">
         <h2>Donor List</h2>
         {donors.length > 0 ? (
           donors.map((donor, index) => (
            <div key={index} className="donor-card">//               <p>
                 <strong>Name:</strong> {donor.DonerName} </p>
               <p><strong>Blood Group:</strong> {donor.DonerBloodGroup} </p>
               <p><strong>City:</strong> {donor.CityName} </p>
               <p><strong>Contact No:</strong> {donor.ContactNo}</p>
             </div>
           ))
         ) : (<p>No donors found</p>
        )}
       </div>
       <footer className="footer">
         <div className="footer-container">
           <p>&copy; {new Date().getFullYear()} Blood Bank | All Rights Reserved</p>
           <p>
           </p>
         </div>
       </footer>
     </div> 
  )}
    

export default HomePage;
