import React, { useState } from "react";
import { collection, getDocs, query, where } from "firebase/firestore"; // Firestore functions
import '../styles/home.css';
import '../styles/hero.css';
import { db } from "../init-firebase";
import Swal from "sweetalert2";

const Hero = () => {
  const [selectedBloodGroup, setSelectedBloodGroup] = useState('');
  const [showReceivers, setShowReceivers] = useState(false);
  const [receivers, setReceivers] = useState([]); // State to store receivers fetched from Firestore

  // Handle blood group change
  const handleBloodGroupChange = (e) => {
    setSelectedBloodGroup(e.target.value);
    setShowReceivers(false); 
  };

  const fetchUsers = async () => {
    try {
      const querySnapshot = await getDocs(collection(db, "users"));
      
      const usersData = querySnapshot.docs.map(doc => doc.data()); // Get all users data
      
      console.log("Fetched Users: ", usersData); // Log the users' data
      
      return usersData; // Return the data if needed for rendering or processing
    } catch (error) {
      console.error("Error fetching users: ", error);
    }
  };
  
 

  return (
    <section className="hero">
      <div className="hero-text">
        <h2>Give Blood, Save Lives!</h2>
        <p>Your donation can make a difference to someone in need.</p>
        
        <div className="donate-section">
          <h2>Donate Blood</h2>
          <p>Choose your blood group</p>

          <div className="dropdown-container">
            <label htmlFor="blood-group">Blood Group: </label>
            <select
              id="blood-group"
              value={selectedBloodGroup}
              onChange={handleBlodGroupChange}
            >
              <option value="">Select Blood Group</option>
              <option value="A+">A+</option>
              <option value="A-">A-</option>
              <option value="B+">B+</option>
              <option value="B-">B-</option>
              <option value="O+">O+</option>
              <option value="O-">O-</option>
              <option value="AB+">AB+</option>
              <option value="AB-">AB-</option>
            </select>
          </div>

          <button className="cta-btn" onClick={handleDonateClick}>Donate Now</button>
          <button className="cta-btn">Find Donors</button>
        </div>

        {/* Conditionally render receivers based on selected blood group */}
        {showReceivers && receivers.length > 0 && (
          <div className="receivers">
           {receivers.map((receiver, index) => (
  <div key={index} className="receiver-card">
    <h3>{receiver.name}</h3>
    <p>Blood Group: {receiver.bloodGroup}</p>
    <p>Location: {receiver.location}</p>
  </div>
))}

          </div>
        )}

        {/* Show a message if no receivers are found */}
        {showReceivers && receivers.length === 0 && (
         Swal.fire('No User Found of This Group')
        )}
      </div>
    </section>
  );
};

export default Hero;
