import React, { useState, useEffect } from "react";
import { collection, addDoc, getDocs, query, where } from "firebase/firestore";
import { auth, db } from "../init-firebase";
import Swal from "sweetalert2";
import { onAuthStateChanged, signOut } from "firebase/auth";
import { Helmet } from "react-helmet";
import "../styles/home.css";
const HomePage = () => {
  const [bloodGroup, setBloodGroup] = useState("");
  const [name, setName] = useState("");
  const [age, setAge] = useState("");
  const [contactNo, setContactNo] = useState("");
  const [email, setEmail] = useState("");

  const DonateBlood = async () => {
    const user = auth.currentUser; // Check if the user is logged in
    
    if (user) {
      const currentProfileUser = user.email.trim().toLowerCase(); // Ensure email is in lowercase
      console.log("Logged-in user email:", currentProfileUser);
  
      try {
        // Query Firestore to find the user document by email
        const q = query(
          collection(db, "SinupUser"),
          where("UserEmail", "==", currentProfileUser.toLowerCase()) // Ensure toLowerCase is used here
        );
        const querySnapshot = await getDocs(q);
        console.log("Query Snapshot: ", querySnapshot.docs);
  
        if (!querySnapshot.empty) {
          const userDoc = querySnapshot.docs[0];
          const userData = userDoc.data();
          console.log("User details found:", userData);
  
          // Check if the user has already donated
          const donorQuery = query(
            collection(db, "BloodDoners"),
            where("UserEmail", "==", userData.UserEmail)
          );
          const donorSnapshot = await getDocs(donorQuery);
          console.log("DonorSnapshot empty:", donorSnapshot.empty);
  
          if (!donorSnapshot.empty) {
            Swal.fire("You have already donated blood!");
          } else {
            // Validate blood group input
            if (!bloodGroup || bloodGroup === "-- Select Blood Group --") {
              Swal.fire("Please Select Your Blood Group!");
            } else {
              try {
                // Add to BloodDoners collection
                await addDoc(collection(db, "BloodDoners"), {
                  DonerName: userData.name,
                  Age: userData.Age,
                  ContactNo: userData.ContactNO,
                  DonerBloodGroup: bloodGroup,
                  UserEmail: userData.UserEmail,
                });
                Swal.fire(`Thank you ${userData.name} for donating blood!`);
                setBloodGroup(""); // Reset blood group input
              } catch (error) {
                console.error("Error saving donation:", error.message);
                Swal.fire("An error occurred while saving donation. Please try again.");
              }
            }
          }
        } else {
          Swal.fire("User details not found in the database!");
        }
      } catch (error) {
        console.error("Error handling donation:", error.message);
        Swal.fire("An error occurred. Please try again.");
      }
    } else {
      // If no user is logged in
      console.log("No user is logged in! Please log in.");
      Swal.fire("You must be logged in to donate blood.");
    }
  };
  
  
  
  
  // LogOut Function
  const LogOut = async () => {
    try {
      await signOut(auth);
      Swal.fire("Success", "You have been logged out.", "success");
      window.location.href = "/login";
    } catch (error) {
      console.error("Error during logout:", error);
      Swal.fire("Error", "Failed to log out. Please try again.", "error");
    }
  };

  return (
    <div className="homepage">
      <Helmet>
        <title>HomePage</title>
      </Helmet>
      <div className="navbar">
        <div className="navbar-brand">Blood Bank</div>
        <div className="navbar-links">
          <a href="/home">Home</a>
          <a href="/profile">Profile</a>
          <a href="/doner">Donors</a>
          <button onClick={LogOut}>Logout</button>
        </div>
      </div>

      <h1 className="title">Blood Bank</h1>
      <div className="form-container">
        <input type="text" placeholder="Name" value={name} onChange={(e)=>setName(e.target.value)} disabled />
        <input type="number" placeholder="Age" value={age} disabled />
        <input type="text" placeholder="Contact No" value={contactNo} disabled />
        <input
          type="text"
          placeholder="Enter Blood Group"
          value={bloodGroup}
          onChange={(e) => setBloodGroup(e.target.value)}
        />
        <div className="button-group">
          <button onClick={DonateBlood} className="btn donate-btn">
            Donate Blood
          </button>
        </div>
      </div>

      <footer className="footer">
        <div className="footer-container">
          <p>&copy; {new Date().getFullYear()} Blood Bank | All Rights Reserved</p>
        </div>
      </footer>
    </div>
  );
};

export default HomePage;
