import React, { useState } from "react";
import Swal from "sweetalert2";
import { db, storage } from "../init-firebase";
import { ref, uploadBytesResumable, getDownloadURL } from "firebase/storage";
import { collection, addDoc } from "firebase/firestore";

const RegistrationForm = () => {
  const [formData, setFormData] = useState({
    name: "",
    fatherName: "",
    address: "",
    phoneNumber: "",
    
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleFileChange = (e) => {
    setFormData({ ...formData, profilePicture: e.target.files[0] });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const { name, fatherName, address, phoneNumber, profilePicture } = formData;

    if (!name || !fatherName || !address || !phoneNumber || !profilePicture) {
      Swal.fire("Error", "Please fill out all fields!", "error");
      return;
    }

    try {
      // Step 1: Upload the profile picture to Firebase Storage
      const storageRef = ref(storage, `profile_pictures/${Date.now()}_${profilePicture.name}`);
      const uploadTask = uploadBytesResumable(storageRef, profilePicture);

      uploadTask.on(
        "state_changed",
        (snapshot) => {
          // Optional: Handle upload progress
          const progress = (snapshot.bytesTransferred / snapshot.totalBytes) * 100;
          console.log(`Upload is ${progress}% done`);
        },
        (error) => {
          console.error("Error uploading profile picture:", error);
          Swal.fire("Error", "Failed to upload profile picture!", "error");
        },
        async () => {
          // Step 2: Get the uploaded image URL
          const downloadURL = await getDownloadURL(uploadTask.snapshot.ref);

          // Step 3: Save the form data to Firestore
          await addDoc(collection(db, "users"), {
            name,
            fatherName,
            address,
            phoneNumber,
            profilePicture: downloadURL, // Save the image URL in Firestore
            createdAt: new Date(),
          });

          Swal.fire("Success", "Registration successful!", "success");
          setFormData({
            name: "",
            fatherName: "",
            address: "",
            phoneNumber: "",
            profilePicture: null,
          });
        }
      );
    } catch (error) {
      console.error("Error saving data:", error);
      Swal.fire("Error", "Failed to save data!", "error");
    }
  };

  return (
    <div className="flex items-center justify-center min-h-screen bg-gray-100">
      <div className="w-full max-w-lg bg-white rounded-lg shadow-md p-8">
        <h2 className="text-2xl font-bold text-gray-800 text-center mb-6">Registration Form</h2>
        <form onSubmit={handleSubmit} className="space-y-4">
          {/* Name Field */}
          <div>
            <label htmlFor="name" className="block text-sm font-medium text-gray-700">Name</label>
            <input
              type="text"
              id="name"
              name="name"
              className="w-full px-4 py-2 mt-2 border rounded-lg focus:ring-blue-500 focus:border-blue-500"
              placeholder="Enter your name"
              value={formData.name}
              onChange={handleChange}
            />
          </div>
          {/* Father's Name Field */}
          <div>
            <label htmlFor="fatherName" className="block text-sm font-medium text-gray-700">Father's Name</label>
            <input
              type="text"
              id="fatherName"
              name="fatherName"
              className="w-full px-4 py-2 mt-2 border rounded-lg focus:ring-blue-500 focus:border-blue-500"
              placeholder="Enter your father's name"
              value={formData.fatherName}
              onChange={handleChange}
            />
          </div>
          {/* Address Field */}
          <div>
            <label htmlFor="address" className="block text-sm font-medium text-gray-700">Address</label>
            <textarea
              id="address"
              name="address"
              className="w-full px-4 py-2 mt-2 border rounded-lg focus:ring-blue-500 focus:border-blue-500"
              placeholder="Enter your address"
              value={formData.address}
              onChange={handleChange}
            ></textarea>
          </div>
          {/* Phone Number Field */}
          <div>
            <label htmlFor="phoneNumber" className="block text-sm font-medium text-gray-700">Phone Number</label>
            <input
              type="tel"
              id="phoneNumber"
              name="phoneNumber"
              className="w-full px-4 py-2 mt-2 border rounded-lg focus:ring-blue-500 focus:border-blue-500"
              placeholder="Enter your phone number"
              value={formData.phoneNumber}
              onChange={handleChange}
            />
          </div>
          {/* Profile Picture Field */}
          <div>
            <label htmlFor="profilePicture" className="block text-sm font-medium text-gray-700">Profile Picture</label>
            <input
              type="file"
              id="profilePicture"
              name="profilePicture"
              className="w-full mt-2"
              onChange={handleFileChange}
            />
          </div>
          {/* Submit Button */}
          <button
            type="submit"
            className="w-full bg-blue-500 hover:bg-blue-600 text-white py-2 px-4 rounded-lg"
          >
            Submit
          </button>
        </form>
      </div>
    </div>
  );
};

export default RegistrationForm;
