import React,{useState} from "react";
import ExpenseForm from './components/expenseform'
import expensList from './components/expenseList'
import expenseItem from './components/expeseitem'
import totalexpense from './components/totalexpense'

function App() {
  const [expense,setexpense]=useState([]);


  const addExpense = (expense) => {
    setexpense((prevExpenses)=>[...prevExpenses,expense])

  }
  const deleteExpense=(id)=>{
    setexpense((prevExpenses)=>prevExpenses.filter(expense=>expense.id !==id))

  }
  return (
   <div>
    <h1>
      Expense Tracker
      <expenseForm onAddExpense={addExpense}/>
      <totalexpense expenses={expense}/>
      <expensList expenses={expense} onDeleteExpense={deleteExpense} />

    </h1>
   </div>
  );
}

export default App;
