import React from "react";
import { useHistory } from "react-router-dom"; // For navigation (if you're using React Router)

const Hero = () => {
  const history = useHistory();

  // Navigate to Donate Blood page
  const handleDonateBloodClick = () => {
    history.push("/donate");  // Assuming '/donate' is the route for blood donation form
  };

  // Navigate to Find Donor page
  const handleFindDonorClick = () => {
    history.push("/find-donor"); // Assuming '/find-donor' is the route to find a donor
  };

  return (
    <div
      className="relative bg-cover bg-center h-screen"
      style={{
        backgroundImage: `url('https://example.com/blood-background.jpg')`, // Replace with your blood-related image URL
      }}
    >
      <div className="absolute inset-0 bg-black opacity-50"></div>
      <div className="relative z-10 flex items-center justify-center h-full text-center text-white">
        <div>
          <h1 className="text-4xl font-bold mb-6">
            Help Save Lives, Donate Blood Today!
          </h1>
          <p className="mb-8 text-lg">
            Your donation can make a huge difference in someone's life.
          </p>
          <div className="flex justify-center gap-8">
            {/* Donate Blood Button */}
            <button
              onClick={handleDonateBloodClick}
              className="px-6 py-3 bg-red-600 hover:bg-red-700 text-white font-semibold rounded-lg shadow-md transition duration-300"
            >
              Donate Blood
            </button>

            {/* Find Donor Button */}
            <button
              onClick={handleFindDonorClick}
              className="px-6 py-3 bg-blue-600 hover:bg-blue-700 text-white font-semibold rounded-lg shadow-md transition duration-300"
            >
              Find Donor
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Hero;
