import React, { useEffect, useState } from "react";
import { db, collection, getDocs } from "../init-firebase"; // Modular Firebase import

const DonersData = () => {
  const [doners, setDoners] = useState([]);

  useEffect(() => {
    const fetchDonors = async () => {
      try {
        const donorCollection = collection(db, "donors");
        const donorSnapshot = await getDocs(donorCollection);
        const donorList = donorSnapshot.docs.map((doc) => doc.data());
        setDoners(donorList);
      } catch (error) {
        console.error("Error fetching donors: ", error);
      }
    };

    fetchDonors();
  }, []); // Empty dependency array to run only once when the component mounts

  return (
    <div>
      <h2>Blood Donor List</h2>
      <div>
        {doners.length === 0 ? (
          <p>No donors found.</p>
        ) : (
          doners.map((doner, index) => (
            <div key={index} className="doner">
              <h3>{doner.Name}</h3>
              <h3>{doner.Age}</h3>
              <h3>{doner.ContactNumber}</h3>
              <h3>{doner.BloodGroup}</h3>
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default DonersData;
