import React, { useState, useEffect } from "react";
import { collection, addDoc, getDocs, query, where, onSnapshot, getDoc, doc, updateDoc } from "firebase/firestore";
import { auth, db } from "../init-firebase";
import Swal from "sweetalert2";
import { Helmet } from "react-helmet";
import { onAuthStateChanged } from "firebase/auth";
import '../styles/home.css';

const HomePage = () => {
  const [bloodGroup, setBloodGroup] = useState("");
  const [city, setCity] = useState("");
  const [canDonate, setCanDonate] = useState(true);
  const [currentUser, setCurrentUser] = useState(null);
  const [donors, setDonors] = useState([]);

  useEffect(() => {
    const fetchDonors = async () => {
      try {
        const donorsCollection = collection(db, "BloodDoners");
        const unsubscribe = onSnapshot(donorsCollection, (snapshot) => {
          const donorData = snapshot.docs.map((doc) => doc.data());
          setDonors(donorData);
        });
        return unsubscribe;
      } catch (error) {
        console.error("Error fetching donors:", error);
        Swal.fire("Error", "Failed to fetch donors data", "error");
      }
    };

    fetchDonors();
  }, []);

  useEffect(() => {
    const unsubscribe = onAuthStateChanged(auth, (user) => {
      if (user) {
        setCurrentUser(user);
        checkDonationStatus(user);
      } else {
        setCurrentUser(null);
      }
    });

    return () => unsubscribe();
  }, []);

  const checkDonationStatus = async (user) => {
    try {
      const userRef = doc(db, "SinupUser", user.uid);
      const userSnap = await getDoc(userRef);

      if (userSnap.exists()) {
        const userData = userSnap.data();
        setCanDonate(!userData.hasDonated);
      } else {
        setCanDonate(true);
      }
    } catch (error) {
      console.error("Error checking donation status:", error);
      setCanDonate(true);
    }
  };

  const DonateButton = async (user) => {
    if (!user) {
      Swal.fire("You must be logged in to donate blood.");
      return;
    }

    try {
      const userRef = doc(db, "SinupUser", user.uid);
      const userSnap = await getDoc(userRef);

      if (userSnap.exists()) {
        const userData = userSnap.data();

        if (!userData.UserBloodGroup || !userData.CityName || !userData.ContactNo) {
          Swal.fire({
            title: "Incomplete Profile",
            text: "Please complete your profile before donating blood.",
            icon: "warning",
            confirmButtonText: "Update Profile",
            preConfirm: () => {
              window.location.href = "/profile";
            },
          });
          return;
        }

        await addDoc(collection(db, "BloodDoners"), {
          DonerName: userData.Name,
          DonerBloodGroup: userData.UserBloodGroup,
          CityName: userData.CityName,
          ContactNo: userData.ContactNo,
          DonationDate: new Date(),
        });

        await updateDoc(userRef, { hasDonated: true });
        Swal.fire(`Thank you ${userData.Name} for your blood donation!`);
      } else {
        Swal.fire("User data not found.");
      }
    } catch (error) {
      console.error("Error donating blood:", error.message);
      Swal.fire("An error occurred while donating blood. Please try again.");
    }
  };

  const FindDoner = async () => {
    if (!bloodGroup || !city) {
      Swal.fire("Please add both city and blood group");
      return;
    }

    try {
      const filters = [];
      if (bloodGroup) filters.push(where("DonerBloodGroup", "==", bloodGroup));
      if (city) filters.push(where("CityName", "==", city));

      const matchData = query(collection(db, "BloodDoners"), ...filters);
      const querySnapshot = await getDocs(matchData);

      const matchedDonors = querySnapshot.docs.map((doc) => ({
        id: doc.id,
        ...doc.data(),
      }));

      if (matchedDonors.length === 0) {
        Swal.fire("No donors found matching your criteria.");
      } else {
        setDonors(matchedDonors);
      }
    } catch (error) {
      console.error("Error fetching donors:", error);
      Swal.fire("Error", "Failed to fetch data", "error");
    }
  };

  return (
    <div className="homepage">
      <Helmet>
        <title>HomePage</title>
      </Helmet>
      <div className="navbar">
        <div className="navbar-brand">Blood Bank</div>
        <div className="navbar-links">
          <a href="#">Home</a>
          <a href="#">Donate</a>
          <a href="/profile">Profile</a>
          <a href="#">Contact Us</a>
        </div>
      </div>

      <h1 className="title">Blood Bank</h1>
      <div className="form-container">
        <input
          type="text"
          placeholder="Enter Blood Group"
          value={bloodGroup}
          onChange={(e) => setBloodGroup(e.target.value)}
        />
        <input
          type="text"
          placeholder="Enter City"
          value={city}
          onChange={(e) => setCity(e.target.value)}
        />
        <div className="button-group">
          <button
            onClick={() => DonateButton(currentUser)}
            disabled={!canDonate}
            className={`btn donate-btn ${canDonate ? "enabled" : "disabled"}`}
          >
            Donate Blood
          </button>
          <button onClick={FindDoner} className="btn find-btn">
            Find Donor
          </button>
        </div>
      </div>

      <div className="donor-list">
        <h2>Donor List</h2>
        {donors.length > 0 ? (
          donors.map((donor, index) => (
            <div key={index} className="donor-card">
              <p><strong>Name:</strong> {donor.DonerName || "N/A"}</p>
              <p><strong>Contact:</strong> {donor.ContactNo || "N/A"}</p>
              <p><strong>Blood Group:</strong> {donor.DonerBloodGroup}</p>
              <p><strong>City:</strong> {donor.CityName}</p>
            </div>
          ))
        ) : (
          <p>No donors found</p>
        )}
      </div>
    </div>
  );
};

export default HomePage;
