import React, { useState } from "react";
import { setDoc, doc, arrayUnion, query, where, getDocs, collection } from "firebase/firestore";
import Swal from "sweetalert2";
import { db } from "../init-firebase";

const Hero = () => {
    const [selectedCity, setSelectedCity] = useState("");
    const [selectedBloodGroup, setSelectedBloodGroup] = useState("");
    const [matchingReceivers, setMatchingReceivers] = useState([]);

  const handleGroupChange = (e) => {
    setSelectedBloodGroup(e.target.value);
  };

  const handleDonateClick = async () => {
    try {
      if (slectBloodGroup) {
        const userRef = doc(db, "users", userData.email);

        await setDoc(userRef, {
          name: userData.name,
          email: userData.email,
          address: userData.address,
          doners: arrayUnion({
            name: userData.name,
            bloodGroup: slectBloodGroup,
            address: userData.address,
            createdAt: new Date(),
          }),
        }, { merge: true });

        Swal.fire("Thank you!", "You have donated successfully.", "success");
        console.log("Donor data saved successfully.");
      } else {
        Swal.fire("Error", "Please select a blood group first.", "error");
      }
    } catch (error) {
      console.error("Error saving donor data: ", error);
      Swal.fire("Error", "Something went wrong.", "error");
    }
  };

  const [matchingDonors, setMatchingDonors] = useState([]);

  const fetchMatchingDonors = async () => {
    try {
      const q = query(collection(db, "users"), where("doners.bloodGroup", "==", slectBloodGroup));
      const querySnapshot = await getDocs(q);

      const matchingDonors = querySnapshot.docs.map(doc => doc.data());
      setMatchingDonors(matchingDonors);
    } catch (error) {
      console.error("Error fetching matching donors: ", error);
    }
  };

  return (
    <section className="hero">
      <div className="hero-text">
        <h2>Give Blood, Save Lives!</h2>
        <p>Your donation can make a difference to someone in need.</p>
        <div className="donate-section">
          <h2>Donate Blood</h2>
          <p>Choose your blood group</p>
          <div className="dropdown-container">
            <label htmlFor="blood-group">Blood Group: </label>
            <select
              id="blood-group"
              value={slectBloodGroup}
              onChange={handleGroupChange}
            >
              <option value="A+">A+</option>
              <option value="A-">A-</option>
              <option value="B+">B+</option>
              <option value="B-">B-</option>
              <option value="O+">O+</option>
              <option value="O-">O-</option>
              <option value="AB+">AB+</option>
              <option value="AB-">AB-</option>
            </select>
          </div>
          <button onClick={handleDonateClick} className="cta-btn">
            Donate Now
          </button>
          <button onClick={fetchMatchingDonors} className="cta-btn">
            Find Donors
          </button>
        </div>
      </div>

      {/* Render matching donors */}
      {matchingDonors.length > 0 && (
        <div className="matching-donors">
          <h3>Matching Donors</h3>
          <div className="donor-cards">
            {matchingDonors.map((donor, index) => (
              <div key={index} className="donor-card">
                <h4>{donor.name}</h4>
                <p>Blood Group: {donor.doners[0].bloodGroup}</p>
                <p>Address: {donor.doners[0].address}</p>
              </div>
            ))}
          </div>
        </div>
      )}
    </section>
  );
};

export default Hero;


export {Hero};
