import React, { useState } from "react";
import { collection, addDoc, getDocs, query, where } from "firebase/firestore";
import '../styles/home.css'
import { db } from "../init-firebase";
const HomePage = () => {
  const [bloodGroup, setBloodGroup] = useState("");
  const [city, setCity] = useState("");
  const [name, setName] = useState("");
  const [age, setAge] = useState("");
  const [ContactNo,setContactNo]=useState('')
  const [donors, setDonors] = useState([]);

  const handleDonate = async () => {
    if (!name || !age || !bloodGroup || !city) {
      alert("Please fill in all fields!");
      return;
    }

    try {
      await addDoc(collection(db, "donors"), {
        name,age,bloodGroup, city,
      });
      alert(`Thank you ${name} For donating blood`);
    } catch (error) {
      console.error("Error saving data:", error);
    }
  };

  const handleFindDonor = async () => {
    if (!bloodGroup || !city) {
      alert("Please enter blood group and city!");
      return;
    }

    try {
      const matchdata = query(
        collection(db, "donors"),
        where("bloodGroup", "==", bloodGroup),
        where("city", "==", city)
      );
      const querySnapshot = await getDocs(matchdata);
      const matchedDonors = querySnapshot.docs.map((doc) => doc.data());
      setDonors(matchedDonors);
    } catch (error) {
      console.error("Error data:", error);
    }
  };

  return (
    <div className="homepage">
      <h1 className="title">Blood Bank</h1>
      <div className="form-container">
        <input type="text" placeholder="Enter Your Name" value={name} onChange={(e) => setName(e.target.value)}
        />
        <input type="number"         placeholder="Enter Your Age"
          value={age}
          onChange={(e) => setAge(e.target.value)}
        />
        <input
          type="text"
          placeholder="Enter Blood Group (e.g., A+)"
          value={bloodGroup}
          onChange={(e) => setBloodGroup(e.target.value)}
        />
        <input
          type="text"
          placeholder="Enter City"
          value={city}
          onChange={(e) => setCity(e.target.value)}
        />
        <div className="button-group">
          <button onClick={handleDonate} className="btn donate-btn">
            Donate Blood
          </button>
          <button onClick={handleFindDonor} className="btn find-btn">
            Find Donor
          </button>
        </div>
      </div>

      <div className="donor-list">
        <h2>Donors Found:</h2>
        {donors.map((donor, index) => (
          <div key={index} className="donor-card">
            <p><strong>Name:</strong> {donor.name}</p>
            <p><strong>Age:</strong> {donor.age}</p>
            <p><strong>Blood Group:</strong> {donor.bloodGroup}</p>
            <p><strong>City:</strong> {donor.city}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default HomePage;
